            fontFamily: styles.typography.fontFamily,
            transition: 'background 0.18s, border 0.15s, box-shadow 0.18s',
            opacity: isSelected ? 1 : 0.99
        };
    }

    // --- EMPTY STATE ---
    if (!prompts.length) {
        // Distinct message for uncategorized
        const uncategorized = config.uncategorized;
        return (
            <div
                style={{
                    height: '100%',
                    display: 'flex',
                    flexDirection: 'column',
                    justifyContent: 'center',
                    alignItems: 'center',
                    background: 'linear-gradient(180deg, #fff, #ffd60022 60%, #e41d2411)',
