            <rect width=\"22\" height=\"8\" x=\"21\" fill=\"#ffe156\" rx=\"2\" />
            <rect width=\"21\" height=\"8\" x=\"43\" fill=\"#d72631\" rx=\"2\" />
        </svg>
    );
    // Card background: white, with bold border and/or rasta gradient accent
    const cardBg = '#fff';
    const cardShadow = '0 3px 20px rgba(33,33,33,0.13)';
