    const UNCATEGORIZED_ICON = '??'; // Puzzle piece for uncategorized

    // ---- Helpers ----
    // Build tree from flat category list
    const buildCategoryTree = (categories) => {
        const nodesById = {};
        const roots = [];
        (categories || []).forEach(cat => {
            nodesById[cat.ID] = { ...cat, children: [] };
        });
        (categories || []).forEach(cat => {
            if (cat.ParentID && nodesById[cat.ParentID]) {
                nodesById[cat.ParentID].children.push(nodesById[cat.ID]);
            } else {
                roots.push(nodesById[cat.ID]);
            }
        });
        return roots;
    };

    // Handle state
    const [localState, setLocalState] = React.useState({
        expanded: {}, // categoryID: true/false
        lastSelectedId: (selectedCategoryID !== undefined ? selectedCategoryID : null),
