                                aria-label={isExpanded ? 'Collapse category' : 'Expand category'}
                                tabIndex={-1}
                                onClick={e => { e.stopPropagation(); handleExpandToggle(node.ID); }}
                                style={{
                                    background: 'none',
                                    border: 'none',
                                    color: isSelected ? CANADA_WHITE : RASTA_GREEN,
                                    cursor: 'pointer',
                                    fontSize: styles.typography.fontSize.md,
                                    marginRight: styles.spacing.sm,
                                    outline: 'none',
                                    width: 24,
                                    height: 24,
                                    display: 'flex',
                                    alignItems: 'center',
                                    justifyContent: 'center',
                                    borderRadius: styles.borders.radius,
                                    transition: 'background 0.12s',
                                }}
                                onMouseEnter={e => e.currentTarget.style.background = `${RASTA_GREEN}22`}
                                onMouseLeave={e => e.currentTarget.style.background = 'none'}
                            >
                                {isExpanded ? (
                                    <span aria-hidden=\"true\" style={{ fontSize: 18, fontWeight: 900 }}>â€“</span>
                                ) : (
                                    <span aria-hidden=\"true\" style={{ fontSize: 18, fontWeight: 900 }}>+</span>
                                )}
                            </button>
                        )}
                        <span style={{
                            fontWeight: isSelected ? 700 : 400,
                            overflow: 'hidden',
                            textOverflow: 'ellipsis',
                            whiteSpace: 'nowrap',
                            letterSpacing: isSelected ? '0.02em' : '0',
                        }}>{node.Name}</span>
                        {/* Canada/Rasta accent bar */}
                        {isSelected && (
                            <span style={{ position: 'absolute', right: 8, fontSize: 16 }}>{LEAF_EMOJI}</span>
                        )}
                    </div>
                    {hasChildren && isExpanded && (
                        <div>
                            {renderTree(node.children, depth + 1)}
                        </div>
                    )}
                </React.Fragment>
            );
        });
    };

    // ---- Main Render ----
    // Categories as tree
    const categories = Array.isArray(data) ? data : (Array.isArray(data?.categories) ? data.categories : []);
    const tree = buildCategoryTree(categories);
    const empty = !categories || categories.length === 0;

    // --- UNCATEGORIZED node ---
    // The 'Uncategorized' node is always present, persistent, and at the top (could move to bottom if desired)
    const isUncategorizedSelected = localState.lastSelectedId === null || localState.lastSelectedId === '__UNCATEGORIZED__';
    const uncategorizedNodeStyle = {
        display: 'flex',
        alignItems: 'center',
        padding: `${styles.spacing.xs} ${styles.spacing.md}`,
        marginBottom: styles.spacing.xs,
        background: isUncategorizedSelected
            ? `linear-gradient(90deg,${RASTA_GREEN} 0%,${RASTA_YELLOW} 70%,${RASTA_RED} 100%)`
            : 'transparent',
        color: isUncategorizedSelected ? CANADA_WHITE : CANADA_RED,
        borderRadius: styles.borders.radius,
        cursor: 'pointer',
        fontWeight: isUncategorizedSelected ? 700 : 500,
        fontFamily: styles.typography.fontFamily,
        fontSize: styles.typography.fontSize.md,
        outline: isUncategorizedSelected ? `2px solid ${RASTA_RED}` : 'none',
        boxShadow: isUncategorizedSelected ? `0 0 0 3px ${RASTA_YELLOW}` : 'none',
        letterSpacing: isUncategorizedSelected ? '0.03em' : '0',
        position: 'relative',
        minHeight: 36,
        border: isUncategorizedSelected ? `2px solid ${CANADA_RED}` : '2px solid transparent',
        transition: 'background 0.15s, color 0.15s, box-shadow 0.18s',
    };
    // For accessibility, allow keyboard select
    const handleUncatKeyDown = (e) => {
        if (e.key === 'Enter' || e.key === ' ') {
            handleSelect(null);
        }
    };

    return (
        <div
            style={{
                height: '100%',
                display: 'flex',
                flexDirection: 'column',
                background: `repeating-linear-gradient(135deg,${CANADA_WHITE},${RASTA_YELLOW} 10%,${CANADA_WHITE} 20%,${RASTA_GREEN} 30%,${CANADA_WHITE} 40%)`,
                borderRight: `4px solid ${CANADA_RED}`,
                padding: styles.spacing.md,
                minWidth: 240,
                maxWidth: 400,
                overflowY: 'auto',
                fontFamily: styles.typography.fontFamily,
                boxShadow: `4px 0 16px -6px ${RASTA_RED}44 inset`,
            }}
        >
            <div style={{
                fontSize: styles.typography.fontSize.lg,
