    );
}
"}],"componentCode":"function CategorySidebar({ data, config, userState, onEvent, styles, callbacks, utilities, statePath, selectedCategoryID }) {
    // <<SUB_TREE_NODE>>

    // --- UNCATEGORIZED NODE CONFIGURATION ---
    // config.uncategorizedNode: { id, label, icon, position }
    const uncategorizedNode = config && config.uncategorizedNode ? config.uncategorizedNode : {
        id: '__UNCATEGORIZED__',
        label: 'Uncategorized',
        icon: '??',
        position: 'bottom' // or 'top'
    };

    // --- BUILD CATEGORY TREE ---
    const buildCategoryTree = (categories) => {
        const nodesById = {};
        const roots = [];
        (categories || []).forEach(cat => {
            nodesById[cat.ID] = { ...cat, Children: [] };
        });
        (categories || []).forEach(cat => {
            if (cat.ParentID && nodesById[cat.ParentID]) {
                nodesById[cat.ParentID].Children.push(nodesById[cat.ID]);
            } else {
                roots.push(nodesById[cat.ID]);
            }
        });
        return roots;
    };
    const categories = Array.isArray(data) ? data : (Array.isArray(data?.categories) ? data.categories : []);
    const treeRoots = buildCategoryTree(categories);
    const empty = !categories || categories.length === 0;

    // --- STATE ---
    const [localState, setLocalState] = React.useState({
        expanded: {},
        focusedNodeId: null,
        ...userState
    });

    // --- STATE SYNC ---
    React.useEffect(() => {
        // If selection is changed from parent, focus selected node
        if (selectedCategoryID && selectedCategoryID !== localState.focusedNodeId) {
            setLocalState(prev => ({ ...prev, focusedNodeId: selectedCategoryID }));
        }
    }, [selectedCategoryID]);

    // --- EVENT HELPERS ---
    const updateState = (updates) => {
        const newState = { ...localState, ...updates };
        setLocalState(newState);
        onEvent({
            type: 'stateChanged',
            source: 'CategorySidebar',
            payload: { statePath, newState },
            bubbles: true
        });
    };

    // --- UNCATEGORIZED SELECTION ---
    const handleUncategorizedSelect = () => {
        updateState({ focusedNodeId: uncategorizedNode.id });
        onEvent({
            type: 'itemSelected',
            source: 'CategorySidebar',
            payload: { itemId: uncategorizedNode.id },
            bubbles: true
        });
    };

    // --- KEYBOARD NAVIGATION ---
    // We'll keep a flat list of visible node ids for up/down navigation
    const flattenVisibleNodes = (nodes, result = [], level = 0) => {
        nodes.forEach(n => {
            result.push({ id: n.ID, level });
            if (localState.expanded[n.ID] && Array.isArray(n.Children) && n.Children.length > 0) {
                flattenVisibleNodes(n.Children, result, level + 1);
            }
        });
        return result;
    };
    const visibleNodes = flattenVisibleNodes(treeRoots);
    // Add the uncategorized node at the top or bottom as per config
    if (uncategorizedNode.position === 'top') {
        visibleNodes.unshift({ id: uncategorizedNode.id, level: 0 });
    } else {
        visibleNodes.push({ id: uncategorizedNode.id, level: 0 });
    }
    // Find index of current focused node
    const focusedIdx = visibleNodes.findIndex(n => n.id === localState.focusedNodeId);

    // Keyboard handler on container
    const handleSidebarKeyDown = (e) => {
        if (!visibleNodes.length) return;
        // Up/Down arrow navigation
        if (e.key === 'ArrowDown') {
            e.preventDefault();
            const next = (focusedIdx + 1) % visibleNodes.length;
            const newId = visibleNodes[next].id;
            setLocalState(prev => ({ ...prev, focusedNodeId: newId }));
        } else if (e.key === 'ArrowUp') {
            e.preventDefault();
            const prevIdx = (focusedIdx - 1 + visibleNodes.length) % visibleNodes.length;
            const newId = visibleNodes[prevIdx].id;
            setLocalState(prev => ({ ...prev, focusedNodeId: newId }));
        } else if (e.key === 'Enter' || e.key === ' ') {
            e.preventDefault();
            const nodeId = visibleNodes[focusedIdx]?.id;
            if (nodeId === uncategorizedNode.id) {
                handleUncategorizedSelect();
            } else if (nodeId) {
                onEvent({
                    type: 'itemSelected',
                    source: 'CategorySidebar',
                    payload: { itemId: nodeId },
                    bubbles: true
                });
            }
        }
    };

    // --- RENDERING ---
    // Vibrant Canadian & Rasta color accents
    const rastaCanadianGradient = uncategorizedNode.position === 'top'
        ? 'linear-gradient(90deg, #D32F2F 0%, #FFD600 33%, #388E3C 66%, #FF7043 100%)'
        : 'linear-gradient(90deg, #43A047 0%, #FFD600 33%, #D32F2F 66%, #C62828 100%)';

    // UNCATEGORIZED visually distinct node
    const renderUncategorizedNode = (isSelected, isFocused) => (
        <div
            key={uncategorizedNode.id}
            role=\"treeitem\"
            tabIndex={0}
            aria-selected={isSelected}
            aria-label={uncategorizedNode.label}
