            onKeyDown={e => {
                if (e.key === 'Enter' || e.key === ' ') handleUncategorizedSelect();
            }}
            style={{
                display: 'flex',
                alignItems: 'center',
                gap: styles.spacing.sm,
                padding: `${styles.spacing.sm} ${styles.spacing.md}`,
                marginTop: uncategorizedNode.position === 'top' ? 0 : styles.spacing.lg,
                marginBottom: uncategorizedNode.position === 'top' ? styles.spacing.lg : 0,
                background: isSelected ? rastaCanadianGradient : 'linear-gradient(90deg, #fff 0%, #FFFDE7 100%)',
                color: isSelected ? '#fff' : '#BF360C',
                border: isSelected
                    ? `2px solid #FFD600`
                    : `2px solid transparent`,
                borderRadius: '99px',
                fontWeight: 700,
                fontSize: styles.typography.fontSize.lg,
                letterSpacing: '0.01em',
                boxShadow: isSelected ? '0 2px 12px 0 rgba(211,47,47,0.22)' : 'none',
