            onFocus={() => setLocalState(prev => ({ ...prev, focusedNodeId: uncategorizedNode.id }))}
        >
            {/* Maple leaf with reggae stripes icon */}
            <span style={{ fontSize: '1.5em', marginRight: styles.spacing.sm, filter: isSelected ? 'drop-shadow(0 2px 4px #222)' : 'none' }}>
                {uncategorizedNode.icon}
            </span>
            <span style={{ flex: 1, textShadow: isSelected ? '1px 1px 2px #2226' : 'none' }}>
                {uncategorizedNode.label}
