            {/* Mini rasta badge */}
            <span aria-hidden=\"true\" style={{
                display: 'inline-block',
                width: 20,
                height: 20,
                marginLeft: styles.spacing.sm,
                borderRadius: '50%',
                background: 'linear-gradient(135deg,#388E3C 0%, #FFD600 50%, #D32F2F 100%)',
                boxShadow: isSelected ? '0 0 0 2px #FFD600' : 'none',
            }}></span>
        </div>
    );

    // --- TREE NODE RENDER ---
    // Recursively render the tree via TreeNode placeholder
    const renderTreeNodes = (nodes, level = 0) => {
        if (!nodes || nodes.length === 0) return null;
        return nodes.map((node) => (
            <React.Fragment key={node.ID}>
                <TreeNode
                    data={node}
                    config={{
                        level,
                        selectedCategoryID,
                        focusedNodeId: localState.focusedNodeId
                    }}
                    userState={userState?.[node.ID] || {}}
                    onEvent={onEvent}
                    styles={styles}
                    callbacks={callbacks}
                    utilities={utilities}
                    statePath={`${statePath}.${node.ID}`}
                />
                {Array.isArray(node.Children) && node.Children.length > 0 && localState.expanded[node.ID] && (
                    <div>
                        {renderTreeNodes(node.Children, level + 1)}
                    </div>
                )}
            </React.Fragment>
        ));
    };

    // --- MAIN RENDER ---
    // Compose node list with uncategorized at top/bottom
    const nodeList = [];
    if (uncategorizedNode.position === 'top') {
        nodeList.push(renderUncategorizedNode(selectedCategoryID === uncategorizedNode.id, localState.focusedNodeId === uncategorizedNode.id));
    }
    if (!empty) {
        nodeList.push(renderTreeNodes(treeRoots));
    } else {
        nodeList.push(
            <div key=\"no-categories\" style={{
                color: styles.colors.textSecondary,
                textAlign: 'center',
                padding: styles.spacing.xl,
