    }
    if (uncategorizedNode.position === 'bottom') {
        nodeList.push(renderUncategorizedNode(selectedCategoryID === uncategorizedNode.id, localState.focusedNodeId === uncategorizedNode.id));
    }

    // --- SIDEBAR STYLING ---
    return (
        <div
            style={{
                height: '100%',
                display: 'flex',
                flexDirection: 'column',
                background: '#FFF',
                borderRight: `${styles.borders.width} solid ${styles.colors.border}`,
                padding: styles.spacing.md,
                minWidth: 240,
                maxWidth: 400,
                overflowY: 'auto',
                fontFamily: styles.typography.fontFamily,
