            }}>
                <span style={{ fontSize: '1.1em', marginRight: styles.spacing.sm }}>??</span>
                <span>Categories</span>
            </div>
            <div style={{ flex: 1, display: 'flex', flexDirection: 'column', gap: styles.spacing.xs }}>
                {nodeList}
            </div>
        </div>
    );
}
