    // --- Canadian-Rasta color palette (bold, high-contrast) ---
    // Canadian: vibrant red, white, maple leaf icon; Rasta: bold green, yellow, red
    // We'll use gradients, icons, and color blocks for energy!
    const CANADIAN_RED = '#D32E2E';
    const MAPLE_LEAF = '??'; // Unicode fallback, could use SVG if available
    const RASTA_GREEN = '#009E49';
    const RASTA_YELLOW = '#FFD600';
    const RASTA_RED = '#EF3340';
    const RASTA_GRADIENT = `linear-gradient(90deg, ${RASTA_GREEN} 0%, ${RASTA_YELLOW} 50%, ${RASTA_RED} 100%)`;
    const CANADA_GRADIENT = `linear-gradient(90deg, ${CANADIAN_RED} 0%, #fff 60%, ${CANADIAN_RED} 100%)`;
    const CARD_BORDER_WIDTH = '3px';
