        });
    };
    const handleSelect = (id) => {
        updateState({ selectedId: id });
        onEvent({
            type: 'itemSelected',
            source: 'CategorySidebarWithUncategorized',
            payload: { itemId: id },
            bubbles: true
        });
    };
    const handleExpandToggle = (id) => {
        const expanded = { ...localState.expanded, [id]: !localState.expanded[id] };
        updateState({ expanded });
    };
    const handleFocus = (id) => {
        updateState({ focusedId: id });
    };

    // ---- Keyboard Navigation ----
    // Flatten tree for navigation (including Uncategorized at end)
    function flattenTree(nodes, depth = 0, acc = []) {
        for (const node of nodes) {
            acc.push({ id: node.ID, name: node.Name, depth, hasChildren: node.children?.length > 0 });
            if (node.children && localState.expanded[node.ID]) {
                flattenTree(node.children, depth + 1, acc);
            }
        }
        return acc;
    }

    // Prepare data
    const categories = Array.isArray(data) ? data : [];
    const hasCategories = categories.length > 0;
    const tree = buildCategoryTree(categories);
    let flatNav = flattenTree(tree);
    // Always append Uncategorized at the end
    flatNav.push({ id: UNCATEGORIZED_ID, name: UNCATEGORIZED_LABEL, depth: 0, hasChildren: false });
    // Find focused index, fallback to selected node or first
    const focusedIdx = flatNav.findIndex(x => x.id === localState.focusedId) >= 0 ? flatNav.findIndex(x => x.id === localState.focusedId) : flatNav.findIndex(x => x.id === localState.selectedId);

    // Keyboard navigation handler
    const handleTreeKeyDown = (e) => {
        let idx = focusedIdx >= 0 ? focusedIdx : 0;
        if (e.key === 'ArrowDown') {
            e.preventDefault();
            idx = Math.min(flatNav.length - 1, idx + 1);
            handleFocus(flatNav[idx].id);
        } else if (e.key === 'ArrowUp') {
            e.preventDefault();
            idx = Math.max(0, idx - 1);
