    const isUncategorized = config && (config.selectedCategoryID === null || typeof config.selectedCategoryID === 'undefined');

    // Keyboard focus management
    useEffect(() => {
        // Reset focused index if selection changes or prompts list changes
        setLocalState((prev) => ({
            ...prev,
            focusedIndex: prompts.findIndex(p => p.ID === selectedPromptID) >= 0
                ? prompts.findIndex(p => p.ID === selectedPromptID)
                : 0
        }));
    }, [prompts, selectedPromptID]);

