    const updateState = (updates) => {
        const newState = { ...localState, ...updates };
        setLocalState(newState);
        onEvent({
            type: 'stateChanged',
            source: 'PromptListThemed',
            payload: {
                statePath: statePath,
                newState
            },
            bubbles: true
        });
