function createComponent(React, ReactDOM, useState, useEffect, useCallback, createStateUpdater, createStandardEventHandler) {
  'use strict';

  function AIPromptViewerFixed({ data, utilities, userState, callbacks, styles }) {
    const UNCATEGORIZED_ID = '__UNCATEGORIZED__';
    const [fullUserState, setFullUserState] = useState({
      selectedCategoryID: UNCATEGORIZED_ID, 
      selectedPromptID: null,
      sidebar: {},
      promptList: {},
      promptDetail: {},
      ...userState
    });

    const updateUserState = (stateUpdate) => {
      const newState = { ...fullUserState, ...stateUpdate };
      setFullUserState(newState);
      if (callbacks?.UpdateUserState) {
        callbacks.UpdateUserState(newState);
      }
    };

    const handleComponentEvent = (event) => {
      // Sidebar category selection
      if (event.type === 'itemSelected' && event.source === 'CategorySidebarCanadianRasta') {
        updateUserState({
          selectedCategoryID: event.payload.itemId,
          selectedPromptID: null, // Reset prompt selection when category changes
        });
        return;
      }

      // BUG FIX: Prompt selection from the list for drill-down
      if (event.type === 'itemSelected' && event.source === 'PromptListCanadianRasta') {
        updateUserState({
          selectedPromptID: event.payload.itemId,
        });
        return;
      }

      // Back button from detail view
      if (event.type === 'actionTriggered' && event.payload?.action === 'back' && event.source === 'PromptDetailCanadianRasta') {
        updateUserState({
          selectedPromptID: null,
        });
        return;
      }

      // Fallback to standard handler for generic state updates
      const standardHandler = createStandardEventHandler(updateUserState, callbacks);
      standardHandler(event);
    };

    const categories = data?.data_item_0 || [];
    const prompts = data?.data_item_1 || [];

    const filteredPrompts = fullUserState.selectedCategoryID === UNCATEGORIZED_ID
      ? prompts.filter(p => p.CategoryID == null)
      : prompts.filter(p => p.CategoryID === fullUserState.selectedCategoryID);

    const selectedPrompt = fullUserState.selectedPromptID
      ? prompts.find(p => p.ID === fullUserState.selectedPromptID)
      : null;

    // ?? CRITICAL: INSERT PLACEHOLDER STRINGS FOR YOUR CHILD COMPONENTS ??
    <<CATEGORY_SIDEBAR_CANADIAN_RASTA>>
    <<PROMPT_LIST_CANADIAN_RASTA>>
    <<PROMPT_DETAIL_CANADIAN_RASTA>>

    return (
      <div
        style={{
          display: 'flex',
          flexDirection: 'row', // Explicitly set for clarity
          height: '100%',
          fontFamily: styles.typography.fontFamily,
          background: 'linear-gradient(135deg, #e63946 0%, #fff 40%, #009e49 80%, #fddc05 100%)',
        }}
      >
        {/* Main Content Panel (LEFT) */}
        <div
          style={{
            flex: 1,
            overflow: 'auto',
            padding: styles.spacing.xl,
            background: 'linear-gradient(120deg, #fff 60%, #fddc05 100%)',
            height: '100%',
            display: 'flex',
            flexDirection: 'column',
          }}
        >
          {selectedPrompt ? (
            <PromptDetailCanadianRasta
              data={selectedPrompt}
              config={{}}
              userState={fullUserState.promptDetail || {}}
              onEvent={handleComponentEvent}
              styles={styles}
              callbacks={callbacks}
              utilities={utilities}
              statePath="promptDetail"
            />
          ) : (
            <PromptListCanadianRasta
              data={filteredPrompts}
              config={{}}
              userState={fullUserState.promptList || {}}
              onEvent={handleComponentEvent}
              styles={styles}
              callbacks={callbacks}
              utilities={utilities}
              statePath="promptList"
            />
          )}
        </div>

        {/* Sidebar Panel (RIGHT) */}
        <div
          style={{
            width: '340px',
            minWidth: '220px',
            maxWidth: '400px',
            background: 'linear-gradient(120deg, #e63946 0%, #fff 80%)',
            borderLeft: `4px solid #009e49`, // CHANGED from borderRight
            padding: styles.spacing.lg,
            boxSizing: 'border-box',
            overflow: 'auto',
            height: '100%',
            display: 'flex',
            flexDirection: 'column',
          }}
        >
          <CategorySidebarCanadianRasta
            data={categories}
            config={{
              uncategorizedId: UNCATEGORIZED_ID,
            }}
            userState={fullUserState.sidebar || {}}
            onEvent={handleComponentEvent}
            styles={styles}
            callbacks={callbacks}
            utilities={utilities}
            statePath="sidebar"
            selectedCategoryID={fullUserState.selectedCategoryID}
          />
        </div>
      </div>
    );
  }

  return {
    component: AIPromptViewerFixed,
    print: function() { window.print(); },
    refresh: function() { /* optional */ }
  };
}

