function EntityRelationshipGraph({
  relationships,
  currentEntityId,
  highlightedEntities = [],
  showType,
  onEntityHover,
  onEntityClick,
  loading,
  highContrast,
  userState,
  styles,
  callbacks,
  utilities,
  statePath,
  onEvent
}) {
  // Error handling for required external library
  if (!window.d3) {
    return (
      <div
        style={{
          padding: styles?.spacing?.xl || 24,
          color: styles?.colors?.textSecondary || '#888',
          textAlign: 'center'
        }}
      >
        D3 library is required for relationship graph visualization
      </div>
    );
  }
  const d3 = window.d3;

  // State initialization (Zoom, Pan, Selection)
  const [localState, setLocalState] = React.useState({
    zoom: 1,
    pan: { x: 0, y: 0 },
    ...userState
  });
  const svgRef = React.useRef();
  const containerRef = React.useRef();

  // Event handler helper
  const updateState = (nextState) => {
    setLocalState(nextState);
    onEvent && onEvent({
      type: 'stateChanged',
      source: 'EntityRelationshipGraph',
      payload: { statePath, newState: nextState },
      bubbles: true
    });
  };

  // Build nodes/edges from relationships prop
  // Nodes: all unique Parent and Child entities
  // Edges: relationships array
  const nodesMap = {};
  const edges = [];
  relationships.forEach(rel => {
    nodesMap[rel.ParentEntity] = { id: rel.ParentEntity };
    nodesMap[rel.ChildEntity] = { id: rel.ChildEntity };
    edges.push({
      source: rel.ParentEntity,
      target: rel.ChildEntity,
      type: rel.RelationshipType,
      fkField: rel.FKField
    });
  });
  const nodes = Object.values(nodesMap);

  // Highlight: selected and hover effects
  const isNodeHighlighted = (id) => highlightedEntities && highlightedEntities.includes(id);

  // D3 simulation setup
  React.useEffect(() => {
    if (loading || !svgRef.current) return;

    // Remove previous SVG content
    while (svgRef.current.firstChild) svgRef.current.removeChild(svgRef.current.firstChild);

    const width = containerRef.current ? containerRef.current.clientWidth : 800;
    const height = containerRef.current ? containerRef.current.clientHeight : 600;
    const svg = d3.select(svgRef.current)
      .attr('width', width)
      .attr('height', height)
      .attr('tabIndex', 0)
      .attr('role', 'img')
      .attr('aria-label', 'Entity Relationship Graph');

    // Zoom/pan
    const zoomed = (event) => {
      svgGroup.attr('transform', event.transform);
      const { x, y, k } = event.transform;
      updateState({ ...localState, zoom: k, pan: { x, y } });
    };
    const zoom = d3.zoom()
      .scaleExtent([0.2, 2])
      .on('zoom', zoomed);
    svg.call(zoom);
    // Restore previous zoom/pan
    svg.call(zoom.transform, d3.zoomIdentity.translate(localState.pan.x, localState.pan.y).scale(localState.zoom));

    const svgGroup = svg.append('g');

    // D3 force simulation
    const sim = d3.forceSimulation(nodes)
      .force('link', d3.forceLink(edges).id(d => d.id).distance(100))
      .force('charge', d3.forceManyBody().strength(-300))
      .force('center', d3.forceCenter(width / 2, height / 2));
    // Edge rendering
    const edgeGroup = svgGroup.append('g')
      .attr('stroke', highContrast ? styles.colors.primary : styles.colors.border)
      .attr('stroke-width', 2)
      .selectAll('line')
      .data(edges)
      .enter()
      .append('line')
      .attr('marker-end', d => d.type === 'FK' || d.type === 'foreign key' ? 'url(#arrowhead)' : null);

    // Arrowhead marker
    svgGroup.append('defs').append('marker')
      .attr('id', 'arrowhead')
      .attr('viewBox', '0 -5 10 10')
      .attr('refX', 16)
      .attr('refY', 0)
      .attr('markerWidth', 8)
      .attr('markerHeight', 8)
      .attr('orient', 'auto')
      .append('path')
      .attr('d', 'M0,-5L10,0L0,5')
      .attr('fill', highContrast ? styles.colors.primary : styles.colors.border);

    // Node rendering
    const nodeGroup = svgGroup.append('g')
      .selectAll('g')
      .data(nodes)
      .enter()
      .append('g')
      .attr('tabIndex', 0)
      .attr('role', 'button')
      .attr('aria-label', d => `Entity ${d.id}`);
    // Draw circles
    nodeGroup.append('circle')
      .attr('r', 20)
      .attr('fill', d => {
        if (d.id === currentEntityId) return styles.colors.primary;
        if (isNodeHighlighted(d.id)) return styles.colors.secondary;
        return highContrast ? styles.colors.surface : styles.colors.background;
      })
      .attr('stroke', d => d.id === currentEntityId ? styles.colors.primaryHover : styles.colors.border)
      .attr('stroke-width', 2)
      .on('click', (e, d) => {
        if (onEntityClick) onEntityClick(d.id);
        onEvent && onEvent({
          type: 'entitySelected',
          source: 'EntityRelationshipGraph',
          payload: { entityId: d.id },
          bubbles: true
        });
      })
      .on('mouseenter', (e, d) => {
        if (onEntityHover) onEntityHover(d.id, true);
        onEvent && onEvent({
          type: 'entityHovered',
          source: 'EntityRelationshipGraph',
          payload: { entityId: d.id },
          bubbles: true
        });
      })
      .on('mouseleave', (e, d) => {
        if (onEntityHover) onEntityHover(d.id, false);
        onEvent && onEvent({
          type: 'entityHoverLeft',
          source: 'EntityRelationshipGraph',
          payload: { entityId: d.id },
          bubbles: true
        });
      });
    // Draw labels
    nodeGroup.append('text')
      .attr('y', 35)
      .attr('text-anchor', 'middle')
      .attr('font-size', styles.typography.fontSize.sm)
      .attr('fill', styles.colors.text)
      .attr('pointer-events', 'none')
      .text(d => d.id);

    // Edge labels for FK fields & relationship type
    if (showType) {
      svgGroup.append('g')
        .selectAll('text')
        .data(edges)
        .enter()
        .append('text')
        .attr('font-size', styles.typography.fontSize.xs)
        .attr('fill', styles.colors.textSecondary)
        .text(d => d.fkField ? `${d.type}: ${d.fkField}` : d.type)
        .attr('pointer-events', 'none')
        .attr('id', (d, i) => `edge-label-${i}`);
    }
    // Ticking
    sim.on('tick', () => {
      edgeGroup
        .attr('x1', d => d.source.x)
        .attr('y1', d => d.source.y)
        .attr('x2', d => d.target.x)
        .attr('y2', d => d.target.y);
      nodeGroup
        .attr('transform', d => `translate(${d.x},${d.y})`);
      if (showType) {
        svgGroup.selectAll('text[id^="edge-label-"]')
          .attr('x', d => (d.source.x + d.target.x) / 2)
          .attr('y', d => (d.source.y + d.target.y) / 2 - 5);
      }
    });
    // Handle clean-up
    return () => {
      sim.stop();
    };
  }, [loading, relationships, currentEntityId, highlightedEntities, highContrast, showType, localState.zoom, localState.pan]);

  // Keyboard navigation: Arrow keys to pan, +/- for zoom
  React.useEffect(() => {
    const svgEl = svgRef.current;
    if (!svgEl) return;
    const handleKey = (e) => {
      let { x, y } = localState.pan;
      let zoom = localState.zoom;
      if (e.key === 'ArrowLeft') x += 40;
      if (e.key === 'ArrowRight') x -= 40;
      if (e.key === 'ArrowUp') y += 40;
      if (e.key === 'ArrowDown') y -= 40;
      if (e.key === '+') zoom = Math.min(2, zoom + 0.1);
      if (e.key === '-') zoom = Math.max(0.2, zoom - 0.1);
      updateState({ ...localState, pan: { x, y }, zoom });
    };
    svgEl.addEventListener('keydown', handleKey);
    return () => svgEl.removeEventListener('keydown', handleKey);
  }, [localState]);

  // Loading/refresh indicator
  if (loading) {
    return (
      <div style={{ padding: styles.spacing.xl, textAlign: 'center', color: styles.colors.textSecondary }}>
        Loading relationship network...
      </div>
    );
  }
  // Empty state
  if (!nodes.length || !edges.length) {
    return (
      <div style={{ padding: styles.spacing.xl, textAlign: 'center', color: styles.colors.textSecondary }}>
        No entity relationships found
      </div>
    );
  }
  // Main layout, handles height/overflow
  return (
    <div
      ref={containerRef}
      style={{
        height: '100%',
        width: '100%',
        position: 'relative',
        background: styles.colors.surface,
        border: `1px solid ${styles.colors.border}`,
        borderRadius: styles.borders.radius,
        overflow: 'hidden',
        outline: 0
      }}
    >
      <svg
        ref={svgRef}
        style={{ width: '100%', height: '100%', display: 'block', background: 'none' }}
        tabIndex={0}
        aria-label="Entity Relationship Graph"
      />
    </div>
  );
}

function PerformanceStatusBar({ isLoading, entityCount, fieldCount, paginatedEntities, highContrast, userState, statePath, onEvent, styles, callbacks, utilities }) {
    const [localState, setLocalState] = React.useState({ ...userState });
    const thresholds = {
        entity: 1000,
        field: 60
    };
    // Compose warning messages if thresholds exceeded
    const entityWarn = typeof entityCount === 'number' && entityCount > thresholds.entity;
    const fieldWarn = typeof fieldCount === 'number' && fieldCount > thresholds.field;
    const showWarning = entityWarn || fieldWarn;
    const warningMsg = [
        entityWarn ? `Entity count (${entityCount}) exceeds recommended threshold (${thresholds.entity})` : null,
        fieldWarn ? `Field count (${fieldCount}) exceeds recommended threshold (${thresholds.field})` : null
    ].filter(Boolean).join(' and ');
    // Accessible color/theme
    const containerStyle = {
        display: 'flex',
        alignItems: 'center',
        gap: styles.spacing.md,
        padding: styles.spacing.sm,
        width: '100%',
        boxSizing: 'border-box',
        background: highContrast ? styles.colors.surface : styles.colors.background, 
        borderBottom: `1px solid ${styles.colors.border}`,
        fontSize: styles.typography.fontSize.sm,
        color: styles.colors.text,
        minHeight: '40px'
    };
    const statStyle = {
        display: 'flex',
        alignItems: 'center',
        gap: styles.spacing.xs,
        background: styles.colors.surface,
        borderRadius: styles.borders.radius,
        padding: `0 ${styles.spacing.sm}`,
        height: '28px',
        color: styles.colors.textSecondary
    };
    // Spinner (use Ant Design if available, else fallback to Unicode)
    const Spinner = () => {
        if (window.antd?.Spin) {
            return React.createElement(window.antd.Spin, { size: 'small' });
        }
        return <span style={{ fontSize: '18px', marginRight: styles.spacing.xs }} aria-label="loading" role="status">‚è≥</span>;
    };
    // Progress bar (for loading/pagination)
    function ProgressBar({ percent }) {
        return (
            <div style={{
                width: '60px',
                height: '6px',
                borderRadius: styles.borders.radius,
                background: styles.colors.borderLight || '#eee',
                marginLeft: styles.spacing.xs,
                marginRight: styles.spacing.sm,
                overflow: 'hidden',
                position: 'relative'
            }}>
                <div style={{
                    width: `${Math.min(100, Math.max(0, percent))}%`,
                    height: '100%',
                    background: styles.colors.primary,
                    transition: 'width 0.2s',
                    borderRadius: styles.borders.radius,
                    position: 'absolute',
                    top: 0, left: 0
                }}/>
            </div>
        );
    }
    // Pagination status display
    let pageStats = null;
    if (paginatedEntities && typeof paginatedEntities.total === 'number') {
        const { page = 1, pageSize = 50, total = 0 } = paginatedEntities;
        const shown = Math.min(total, page * pageSize);
        const percent = total > 0 ? (shown / total) * 100 : 0;
        pageStats = (
            <div style={statStyle} title={`Page ${page}, Showing ${shown} of ${total}`}>
                <span>{`Page ${page}`}</span>
                <ProgressBar percent={percent} />
                <span style={{color: styles.colors.textTertiary}}>{`${shown} / ${total}`}</span>
            </div>
        );
    }
    // Compose status display
    return (
        <div style={containerStyle} role="status" aria-live="polite" aria-busy={!!isLoading}>
            {isLoading && <Spinner />}
            <div style={statStyle} title="Entity Count" aria-label={`Entity count: ${entityCount}`}>üóÇ <span>{entityCount}</span> Entities</div>
            <div style={statStyle} title="Field Count" aria-label={`Field count: ${fieldCount}`}>üè∑ <span>{fieldCount}</span> Fields</div>
            {pageStats}
            {showWarning && (
                <div
                    style={{
                        ...statStyle,
                        background: styles.colors.warningLight || '#fffbe6',
                        color: styles.colors.warning || '#ad8b00',
                        fontWeight: 600,
                        marginLeft: styles.spacing.lg, 
                        padding: `${styles.spacing.xs} ${styles.spacing.md}`,
                        border: `1px solid ${styles.colors.warning || '#faad14'}`,
                        boxShadow: styles.shadows?.sm || '0 1px 3px #faad1444'
                    }}
                    role="alert"
                    aria-live="assertive"
                >
                    ‚ö†Ô∏è {warningMsg}
                </div>
            )}
        </div>
    );
}

function RecentEntitiesPanel({ recentEntities, onSelectEntity, highContrast, userState, statePath, onEvent, styles, callbacks, utilities }) {
    const [localState, setLocalState] = React.useState({ ...userState });

    // Key handler state
    const listRef = React.useRef();
    const [focusedIndex, setFocusedIndex] = React.useState(-1);

    // State update utility
    const updateState = (next) => {
        setLocalState(next);
        if (onEvent) {
            onEvent({
                type: 'stateChanged',
                source: 'RecentEntitiesPanel',
                payload: {
                    statePath,
                    newState: next
                },
                bubbles: true
            });
        }
    };

    // Accessibility and ARIA helpers
    const getAriaActiveDescendant = () => {
        if (focusedIndex >= 0 && recentEntities && recentEntities.length > focusedIndex) {
            return `recent-entity-${recentEntities[focusedIndex]?.EntityID}`;
        }
        return undefined;
    };

    // Keyboard navigation (up/down/enter)
    const handleKeyDown = (e) => {
        if (!recentEntities || recentEntities.length === 0) return;
        if (e.key === 'ArrowDown') {
            e.preventDefault();
            setFocusedIndex((prev) => Math.min((prev < 0 ? 0 : prev + 1), recentEntities.length - 1));
        } else if (e.key === 'ArrowUp') {
            e.preventDefault();
            setFocusedIndex((prev) => Math.max((prev <= 0 ? 0 : prev - 1), 0));
        } else if (e.key === 'Enter' && focusedIndex >= 0) {
            e.preventDefault();
            const selected = recentEntities[focusedIndex];
            if (selected) {
                handleItemSelect(selected, focusedIndex, true);
            }
        }
    };

    // Focus & ARIA management
    React.useEffect(() => {
        // If focusedIndex changes, scroll selected into view
        if (listRef.current && focusedIndex >= 0) {
            const el = listRef.current.querySelector(`#recent-entity-${recentEntities[focusedIndex].EntityID}`);
            if (el && el.scrollIntoView) {
                el.scrollIntoView({ block: 'nearest' });
            }
        }
    }, [focusedIndex, recentEntities]);

    // Handle selection
    const handleItemSelect = (item, idx, viaKeyboard = false) => {
        if (onSelectEntity) {
            onSelectEntity(item);
        } else if (callbacks && callbacks.OpenEntityRecord) {
            callbacks.OpenEntityRecord('Entities', [ { FieldName: 'EntityID', Value: item.EntityID } ]);
        }
        setFocusedIndex(idx);
        // Fire coordinated event
        if (onEvent) {
            onEvent({
                type: 'itemSelected',
                source: 'RecentEntitiesPanel',
                payload: {
                    itemId: item.EntityID,
                    item
                },
                bubbles: true
            });
        }
        // Optionally persist focus on keyboard selection
        if (!viaKeyboard && listRef.current) {
            listRef.current.focus();
        }
    };

    // Rendering logic
    if (!recentEntities || recentEntities.length === 0) {
        return (
            <div style={{
                padding: styles.spacing.xl,
                textAlign: 'center',
                color: styles.colors.textSecondary,
                background: styles.colors.surface
            }}>
                No recent entities
            </div>
        );
    }

    // Accessibility styling helpers
    const mainColor = highContrast ? styles.colors.textInverse || '#fff' : styles.colors.text;
    const panelBg = highContrast ? styles.colors.primary : styles.colors.surface;
    const highlightBg = highContrast ? styles.colors.primaryHover : styles.colors.primaryLight || styles.colors.primary;
    const selectedColor = highContrast ? styles.colors.textInverse || '#fff' : '#fff';

    return (
        <div
            role="region"
            aria-label="Recent Entities Panel"
            tabIndex={0}
            ref={listRef}
            onKeyDown={handleKeyDown}
            style={{
                height: '100%',
                display: 'flex',
                flexDirection: 'column',
                background: panelBg,
                border: `1px solid ${styles.colors.border}`,
                borderRadius: styles.borders.radius,
                outline: 'none',
                minWidth: '220px',
                maxWidth: '350px',
                overflow: 'hidden',
            }}
        >
            <div style={{
                padding: styles.spacing.sm,
                fontSize: styles.typography.fontSize.lg,
                fontWeight: 600,
                borderBottom: `1px solid ${styles.colors.border}`,
                background: panelBg,
                color: mainColor
            }}>
                Recent Entities
            </div>
            <ul
                ref={listRef}
                tabIndex={0}
                style={{
                    flex: 1,
                    listStyle: 'none',
                    margin: 0,
                    padding: 0,
                    overflowY: 'auto',
                    background: panelBg
                }}
                role="listbox"
                aria-activedescendant={getAriaActiveDescendant()} 
            >
                {recentEntities.map((entity, idx) => {
                    const isSelected = idx === focusedIndex;
                    return (
                        <li
                            id={`recent-entity-${entity.EntityID}`}
                            key={entity.EntityID}
                            tabIndex={-1}
                            role="option"
                            aria-selected={isSelected}
                            style={{
                                display: 'flex',
                                alignItems: 'center',
                                padding: styles.spacing.sm,
                                cursor: 'pointer',
                                background: isSelected ? highlightBg : panelBg,
                                color: isSelected ? selectedColor : mainColor,
                                fontWeight: isSelected ? 600 : 400,
                                outline: isSelected ? `2px solid ${styles.colors.primary}` : 'none',
                                borderBottom: `1px solid ${styles.colors.borderLight || styles.colors.border}`
                            }}
                            onClick={() => handleItemSelect(entity, idx, false)}
                            onFocus={() => setFocusedIndex(idx)}
                            onMouseEnter={() => setFocusedIndex(idx)}
                        >
                            <span style={{ flex: 1, minWidth: 0, whiteSpace: 'nowrap', overflow: 'hidden', textOverflow: 'ellipsis' }}>
                                {entity.Name}
                            </span>
                            <span style={{
                                fontSize: styles.typography.fontSize.sm,
                                color: styles.colors.textSecondary,
                                marginLeft: styles.spacing.md
                            }}>
                                {entity.EntityID}
                            </span>
                        </li>
                    );
                })}
            </ul>
        </div>
    );
}

