function EntityExplorer({ data, utilities, userState, callbacks, styles, components }) {

 console.log('React available:', typeof React);
  console.log('useState available:', typeof useState);
  console.log('useEffect available:', typeof useEffect); 
 const {
    EntityTreeNavigation,
    EntityFilterBar,
    EntityDetailsPanel,
    EntityRelationshipGraph,
    PerformanceStatusBar,
    RecentEntitiesPanel
  } = components;
  const [fullUserState, setFullUserState] = React.useState({
    treeState: {
      expandedEntities: [],
      selectedEntityId: null,
      selectedFieldId: null,
      ...((userState && userState.treeState) || {})
    },
    searchFilter: {
      text: '',
      attributeFilters: {},
      ...((userState && userState.searchFilter) || {})
    },
    detailsPanel: {
      isOpen: false,
      tab: 'summary',
      ...((userState && userState.detailsPanel) || {})
    },
    relationshipState: {
      highlightedEntities: [],
      showType: 'all',
      ...((userState && userState.relationshipState) || {})
    },
    ui: {
      loading: false,
      pagination: {},
      isHighContrast: false,
      recentEntities: [],
      ...((userState && userState.ui) || {})
    },
    ...userState // In case new fields are added in userState
  });

  // Helper for persisting state upwards
  const updateUserState = React.useCallback((stateUpdate) => {
    const newState = typeof stateUpdate === 'function'
      ? stateUpdate(fullUserState)
      : { ...fullUserState, ...stateUpdate };
    setFullUserState(newState);
    if (callbacks && callbacks.UpdateUserState) {
      callbacks.UpdateUserState(newState);
    }
  }, [fullUserState, callbacks]);

  // Event Handler per Skip's event system
  const handleComponentEvent = React.useMemo(() => {
    return (event) => {
      // Enhanced logic for business event forwarding & state
      if (!event || !event.type) return;
      // Tree selection events
      if ((event.type === 'entitySelected' || event.type === 'itemSelected') && event.source === 'EntityTreeNavigation') {
        const selectedEntityId = event.payload?.itemId || event.payload?.entityId;
        updateUserState({
          treeState: {
            ...fullUserState.treeState,
            selectedEntityId: selectedEntityId,
            selectedFieldId: null, // reset field selection on new entity
            expandedEntities: fullUserState.treeState.expandedEntities
          },
          detailsPanel: {
            ...fullUserState.detailsPanel,
            isOpen: !!selectedEntityId,
            tab: 'summary'
          },
          ui: {
            ...fullUserState.ui,
            recentEntities: selectedEntityId
              ? [
                  ...fullUserState.ui.recentEntities.filter(id => id !== selectedEntityId),
                  selectedEntityId
                ].slice(-20) // keep up to 20
              : fullUserState.ui.recentEntities
          }
        });
        return;
      }
      // Field selection (open detail for field)
      if (event.type === 'fieldSelected' && event.source === 'EntityTreeNavigation') {
        const selectedFieldId = event.payload?.fieldId;
        updateUserState({
          treeState: {
            ...fullUserState.treeState,
            selectedFieldId: selectedFieldId
          },
          detailsPanel: {
            ...fullUserState.detailsPanel,
            isOpen: !!selectedFieldId,
            tab: 'summary'
          }
        });
        return;
      }
      // Expand/collapse and lazy load propagate to treeState
      if (event.type === 'expandToggled' && event.source === 'EntityTreeNavigation') {
        updateUserState({
          treeState: {
            ...fullUserState.treeState,
            expandedEntities: event.payload.expandedEntities
          }
        });
        return;
      }
      if (event.type === 'lazyLoadRequested' && event.source === 'EntityTreeNavigation') {
        updateUserState({
          ui: { ...fullUserState.ui, loading: true }
        });
        // Data reload triggers handled in child
        return;
      }
      // Filter/search
      if ((event.type === 'searchChanged' || event.type === 'filterChanged') && event.source === 'EntityFilterBar') {
        updateUserState({
          searchFilter: {
            ...fullUserState.searchFilter,
            ...(event.type === 'searchChanged' ? { text: event.payload.value } : {}),
            ...(event.type === 'filterChanged' ? { attributeFilters: event.payload.value } : {})
          },
          treeState: {
            ...fullUserState.treeState,
            // Auto-expand or highlight handled by child on filters
          }
        });
        return;
      }
      // Details panel
      if (event.type === 'closeDetails' && event.source === 'EntityDetailsPanel') {
        updateUserState({
          detailsPanel: { ...fullUserState.detailsPanel, isOpen: false }
        });
        return;
      }
      // Details tab change
      if (event.type === 'tabChanged' && event.source === 'EntityDetailsPanel') {
        updateUserState({
          detailsPanel: { ...fullUserState.detailsPanel, tab: event.payload.tab }
        });
        return;
      }
      // Recently viewed selection
      if (event.type === 'entitySelected' && event.source === 'RecentEntitiesPanel') {
        const selectedEntityId = event.payload?.itemId || event.payload?.entityId;
        updateUserState({
          treeState: {
            ...fullUserState.treeState,
            selectedEntityId: selectedEntityId,
            selectedFieldId: null
          },
          detailsPanel: {
            ...fullUserState.detailsPanel,
            isOpen: !!selectedEntityId,
            tab: 'summary'
          }
        });
        return;
      }
      // Relationship graph interactions
      if ((event.type === 'entityHovered' || event.type === 'entityClicked') && event.source === 'EntityRelationshipGraph') {
        updateUserState({
          relationshipState: {
            ...fullUserState.relationshipState,
            highlightedEntities: event.payload?.highlightedEntities || []
          }
        });
        if (event.type === 'entityClicked' && event.payload?.entityId) {
          updateUserState({
            treeState: { ...fullUserState.treeState, selectedEntityId: event.payload.entityId },
            detailsPanel: { ...fullUserState.detailsPanel, isOpen: true, tab: 'summary' }
          });
        }
        return;
      }
      // High contrast mode toggle (could be wired to user settings elsewhere)
      if (event.type === 'toggleHighContrast') {
        updateUserState({
          ui: { ...fullUserState.ui, isHighContrast: !fullUserState.ui.isHighContrast }
        });
        return;
      }
      // Fallback: handle stateChanged and all others w/ standard handler
      const stdHandler = createStandardEventHandler(updateUserState, callbacks);
      stdHandler(event);
    };
  }, [fullUserState, updateUserState, callbacks]);

  // Compose next/selected entity & field objects for details panel
  const selectedEntity = React.useMemo(() => {
    const eid = fullUserState.treeState.selectedEntityId;
    return eid ? (data?.entities || []).find(e => e.EntityID === eid) : null;
  }, [data, fullUserState.treeState.selectedEntityId]);
  const selectedField = React.useMemo(() => {
    const fid = fullUserState.treeState.selectedFieldId;
    return fid ? (data?.entityFields || []).find(f => f.ID === fid) : null;
  }, [data, fullUserState.treeState.selectedFieldId]);

  // Provide entity relationships data
  const entityRelationships = data?.entityRelationships || [];

  // Responsive: master-detail split for desktop/tablet, switch to stacked (list/detail) for mobile
  const [isMobile, setIsMobile] = React.useState(false);
  React.useEffect(() => {
    const checkMobile = () => setIsMobile(window.innerWidth < 768);
    checkMobile();
    window.addEventListener('resize', checkMobile);
    return () => window.removeEventListener('resize', checkMobile);
  }, []);

  // Optionally apply high contrast overrides to styles
  const effectiveStyles = React.useMemo(() => {
    if (fullUserState.ui && fullUserState.ui.isHighContrast && styles) {
      // Override some palette for high contrast needs
      return {
        ...styles,
        colors: {
          ...styles.colors,
          background: '#000',
          surface: '#000',
          border: '#fff',
          text: '#fff',
          textSecondary: '#fff',
          primary: '#FFD600',
          primaryHover: '#FFEA00',
          shadow: '0 0 0 2px #FFD600'
        }
      };
    }
    return styles;
  }, [styles, fullUserState.ui && fullUserState.ui.isHighContrast]);

  // Layout: Split flex for wide, column for mobile
  return (
    <div
      style={{
        height: '100%',
        display: 'flex',
        flexDirection: 'column',
        background: effectiveStyles.colors.background
      }}
      aria-label="Entity Explorer Root"
    >
      {/* Toolbar: Filter Bar & Status */}
      <div style={{
        flexShrink: 0,
        borderBottom: `1px solid ${effectiveStyles.colors.border}`,
        background: effectiveStyles.colors.surface,
        padding: effectiveStyles.spacing.sm,
        display: 'flex',
        flexDirection: isMobile ? 'column' : 'row',
        alignItems: 'center',
        gap: effectiveStyles.spacing.md
      }}>
        <EntityFilterBar
          attributeFilters={fullUserState.searchFilter.attributeFilters}
          searchTerm={fullUserState.searchFilter.text}
          onAttributeFilterChange={handleComponentEvent}
          onSearchInput={handleComponentEvent}
          fieldTypes={data.entitiesFieldTypes} 
          entityTypes={data.entityTypes} 
          securityClasses={data.securityClasses} 
          highContrast={fullUserState.ui.isHighContrast}
          styles={effectiveStyles}
          userState={fullUserState.searchFilter}
          onEvent={handleComponentEvent}
          statePath="searchFilter"
          callbacks={callbacks}
          utilities={utilities}
        />
        <div style={{ flex: 1 }} />
        <PerformanceStatusBar
          isLoading={fullUserState.ui.loading}
          entityCount={data.entities ? data.entities.length : 0}
          fieldCount={data.entityFields ? data.entityFields.length : 0}
          paginatedEntities={fullUserState.ui.pagination}
          highContrast={fullUserState.ui.isHighContrast}
          userState={fullUserState.ui}
          onEvent={handleComponentEvent}
          styles={effectiveStyles}
          statePath="ui"
          callbacks={callbacks}
          utilities={utilities}
        />
      </div>

      {/* Main explorer body */}
      <div
        style={{
          flex: 1,
          display: 'flex',
          flexDirection: isMobile ? 'column' : 'row',
          height: 0,
          minHeight: 0,
          overflow: 'hidden',
          gap: effectiveStyles.spacing.md,
          background: effectiveStyles.colors.background
        }}
      >
        {/* LEFT COLUMN: Tree and recents */}
        <div
          style={{
            width: isMobile ? '100%' : 320,
            minWidth: isMobile ? undefined : 220,
            maxWidth: isMobile ? undefined : 380,
            flex: isMobile ? 'none' : '0 0 320px',
            display: 'flex',
            flexDirection: 'column',
            borderRight: isMobile ? 'none' : `1px solid ${effectiveStyles.colors.border}`,
            background: effectiveStyles.colors.surface,
            height: '100%',
            overflow: 'hidden'
          }}
        >
          <div style={{ flex: 1, minHeight: 0, overflow: 'auto' }}>
            <EntityTreeNavigation
              entities={data.entities}
              expandedEntities={fullUserState.treeState.expandedEntities}
              selectedEntityId={fullUserState.treeState.selectedEntityId}
              selectedFieldId={fullUserState.treeState.selectedFieldId}
              searchTerm={fullUserState.searchFilter.text}
              attributeFilters={fullUserState.searchFilter.attributeFilters}
              onExpandCollapse={handleComponentEvent}
              onSelectEntity={handleComponentEvent}
              onSelectField={handleComponentEvent}
              onLazyLoad={handleComponentEvent}
              loadingStates={fullUserState.ui.loading}
              highContrast={fullUserState.ui.isHighContrast}
              ariaTreeId="entity-explorer-tree"
              styles={effectiveStyles}
              userState={fullUserState.treeState}
              onEvent={handleComponentEvent}
              statePath="treeState"
              callbacks={callbacks}
              utilities={utilities}
            />
          </div>
          <div style={{
            borderTop: `1px solid ${effectiveStyles.colors.border}`,
            background: effectiveStyles.colors.surface,
            padding: effectiveStyles.spacing.sm,
            minHeight: 40
          }}>
            <RecentEntitiesPanel
              recentEntities={fullUserState.ui.recentEntities}
              onSelectEntity={handleComponentEvent}
              highContrast={fullUserState.ui.isHighContrast}
              userState={fullUserState.ui.recentEntities}
              onEvent={handleComponentEvent}
              styles={effectiveStyles}
              statePath="ui.recentEntities"
              callbacks={callbacks}
              utilities={utilities}
            />
          </div>
        </div>
        {/* CENTER COLUMN: Entity Relationships Graph */}
        {!isMobile && (
          <div
            style={{
              minWidth: 0,
              width: 360,
              maxWidth: 540,
              borderRight: `1px solid ${effectiveStyles.colors.border}`,
              background: effectiveStyles.colors.surface,
              display: 'flex',
              flexDirection: 'column',
              height: '100%'
            }}
          >
            <EntityRelationshipGraph
              relationships={entityRelationships}
              currentEntityId={fullUserState.treeState.selectedEntityId}
              highlightedEntities={fullUserState.relationshipState.highlightedEntities}
              showType={fullUserState.relationshipState.showType}
              onEntityHover={handleComponentEvent}
              onEntityClick={handleComponentEvent}
              loading={fullUserState.ui.loading}
              highContrast={fullUserState.ui.isHighContrast}
              userState={fullUserState.relationshipState}
              onEvent={handleComponentEvent}
              styles={effectiveStyles}
              statePath="relationshipState"
              callbacks={callbacks}
              utilities={utilities}
            />
          </div>
        )}
        {/* RIGHT COLUMN: Details Panel (as drawer over content if open) */}
        <div style={{
          flex: 1,
          minWidth: 0,
          position: 'relative',
          height: '100%',
          overflow: 'auto',
          background: effectiveStyles.colors.background
        }}>
          <EntityDetailsPanel
            entity={selectedEntity}
            field={selectedField}
            open={fullUserState.detailsPanel.isOpen}
            tab={fullUserState.detailsPanel.tab}
            onClose={handleComponentEvent}
            onTabChange={handleComponentEvent}
            onViewHistory={handleComponentEvent}
            onViewComparison={handleComponentEvent}
            highContrast={fullUserState.ui.isHighContrast}
            userState={fullUserState.detailsPanel}
            onEvent={handleComponentEvent}
            styles={effectiveStyles}
            statePath="detailsPanel"
            callbacks={callbacks}
            utilities={utilities}
          />
        </div>
      </div>
    </div>
  );
}
