function EntityTree({ data, styles, config, userState, statePath, onEvent, callbacks, utilities }) {
    const { entities = [], expandedIds = [], selectedId, childCounts = {}, filter = {} } = data || {};
    const [localState, setLocalState] = React.useState({
        expandedIds: expandedIds || [],
        filterText: '',
        selectedId: selectedId || null,
        ...userState
    });

    // Helper to emit stateChanged events
    const updateState = (newState) => {
        setLocalState(newState);
        onEvent && onEvent({
            type: 'stateChanged',
            source: 'EntityTree',
            payload: { statePath, newState },
            bubbles: true
        });
    };

    // Handle expand/collapse
    const handleExpand = (id) => {
        const expanded = localState.expandedIds.includes(id)
            ? localState.expandedIds.filter(eid => eid !== id)
            : [...localState.expandedIds, id];
        updateState({ ...localState, expandedIds: expanded });
    };

    // Node selection
    const handleSelect = (id) => {
        updateState({ ...localState, selectedId: id });
        onEvent && onEvent({
            type: 'entitySelected',
            source: 'EntityTree',
            payload: { entityId: id },
            bubbles: true
        });
    };

    // Search/filtering
    const handleFilterChange = (e) => {
        const value = e.target.value;
        updateState({ ...localState, filterText: value });
    };

    // Filtering logic
    const filterText = (localState.filterText || '').toLowerCase();
    const passFilter = (e) => {
        if (!filterText) return true;
        return (
            (e.Name && e.Name.toLowerCase().includes(filterText)) ||
            (e.Description && e.Description.toLowerCase().includes(filterText))
        );
    };

    // Build hierarchical data
    const buildTree = (entities) => {
        const byParent = {};
        entities.forEach(e => {
            byParent[e.ParentID] = byParent[e.ParentID] || [];
            byParent[e.ParentID].push(e);
        });
        const build = (parentId) => {
            (byParent[parentId] || []).sort((a,b) => a.Name.localeCompare(b.Name));
            return (byParent[parentId] || []).map(e => ({
                ...e,
                children: build(e.ID)
            }));
        };
        return build(null);
    };

    // After filtering apply hierarchy
    const filteredEntities = entities.filter(passFilter);
    // We want to only show nodes that match filter or whose descendants match filter
    function pruneTree(nodes) {
        return nodes.map(node => {
            const children = pruneTree(node.children || []);
            const matches = passFilter(node) || children.length > 0;
            return matches ? { ...node, children } : null;
        }).filter(Boolean);
    }

    const fullTree = buildTree(entities);
    const displayTree = filterText ? pruneTree(fullTree) : fullTree;

    // Render tree node
    const renderNode = (node, level = 0) => {
        const isExpanded = localState.expandedIds.includes(node.ID);
        const hasChildren = (node.children && node.children.length > 0);
        const isSelected = localState.selectedId === node.ID;
        // Permission/visibility status
        const badgeCount = childCounts && childCounts[node.ID] ? childCounts[node.ID] : 0;
        let visibilityIcon = '';
        if (typeof node.Permission !== 'undefined') {
            visibilityIcon = node.Permission === 'readonly' ? 'üëÅÔ∏è' : (node.Permission === 'edit' ? '‚úèÔ∏è' : '');
        }
        return (
            <div
                key={node.ID}
                role="treeitem"
                aria-expanded={hasChildren ? isExpanded : undefined}
                aria-selected={isSelected}
                tabIndex={0}
                style={{
                    display: 'flex',
                    alignItems: 'center',
                    marginLeft: level * 18,
                    background: isSelected ? styles.colors.primaryLight : 'none',
                    color: isSelected ? styles.colors.primary : styles.colors.text,
                    padding: styles.spacing.xs + ' ' + styles.spacing.sm,
                    borderRadius: styles.borders.radius,
                    outline: 'none',
                    cursor: 'pointer',
                    fontWeight: isSelected ? 600 : 400
                }}
                onClick={() => handleSelect(node.ID)}
                onKeyDown={(e) => {
                    if (e.key === 'Enter' || e.key === ' ') {
                        handleSelect(node.ID);
                        e.preventDefault();
                    } else if ((e.key === 'ArrowRight' || e.key === '+') && hasChildren && !isExpanded) {
                        handleExpand(node.ID);
                        e.preventDefault();
                    } else if ((e.key === 'ArrowLeft' || e.key === '-') && hasChildren && isExpanded) {
                        handleExpand(node.ID);
                        e.preventDefault();
                    }
                }}
            >
                {hasChildren && (
                    <span
                        role="button"
                        aria-label={isExpanded ? 'Collapse' : 'Expand'}
                        onClick={e => { e.stopPropagation(); handleExpand(node.ID); }}
                        style={{
                            marginRight: 6,
                            fontSize: styles.typography.fontSize.sm,
                            userSelect: 'none',
                            cursor: 'pointer',
                            color: styles.colors.textSecondary
                        }}
                    >
                        {isExpanded ? '‚ñº' : '‚ñ∂'}
                    </span>
                )}
                <span style={{ flex: 1, minWidth: 0, textOverflow: 'ellipsis', overflow: 'hidden', whiteSpace: 'nowrap' }}>{node.Name}</span>
                {badgeCount > 0 && (
                    <span
                        style={{
                            background: styles.colors.primary,
                            color: '#fff',
                            borderRadius: '12px',
                            padding: '0 7px',
                            marginLeft: 8,
                            fontSize: styles.typography.fontSize.sm,
                            minWidth: '20px',
                            textAlign: 'center'
                        }}
                        aria-label={badgeCount + ' child entities'}
                    >{badgeCount}</span>
                )}
                {visibilityIcon && (
                    <span
                        title={node.Permission}
                        aria-label={node.Permission}
                        style={{ marginLeft: 8, fontSize: styles.typography.fontSize.md }}
                    >{visibilityIcon}</span>
                )}
            </div>
        );
    };

    // Render tree recursively
    const renderTree = (nodes, level = 0) => {
        if (!nodes || nodes.length === 0) return null;
        return nodes.map(node => (
            <React.Fragment key={node.ID}>
                {renderNode(node, level)}
                {node.children && node.children.length > 0 && localState.expandedIds.includes(node.ID) && (
                    renderTree(node.children, level + 1)
                )}
            </React.Fragment>
        ));
    };

    return (
        <div
            style={{
                height: '100%',
                display: 'flex',
                flexDirection: 'column',
                fontFamily: styles.typography.fontFamily
            }}
        >
            <div style={{
                display: 'flex',
                alignItems: 'center',
                padding: styles.spacing.sm,
                borderBottom: `1px solid ${styles.colors.border}`,
                background: styles.colors.surface,
                gap: styles.spacing.sm
            }}>
                <input
                    type="search"
                    aria-label="Filter entities"
                    placeholder="Search entities..."
                    value={localState.filterText || ''}
                    onChange={handleFilterChange}
                    style={{
                        flex: 1,
                        padding: styles.spacing.xs + ' ' + styles.spacing.sm,
                        border: `1px solid ${styles.colors.border}`,
                        borderRadius: styles.borders.radius,
                        background: styles.colors.surface,
                        fontSize: styles.typography.fontSize.md,
                        color: styles.colors.text
                    }}
                />
            </div>
            <div
                role="tree"
                aria-label="Entity tree"
                style={{ flex: 1, overflow: 'auto', padding: styles.spacing.md }}
            >
                {displayTree && displayTree.length > 0 ? (
                    renderTree(displayTree)
                ) : (
                    <div style={{
                        padding: styles.spacing.xl,
                        textAlign: 'center',
                        color: styles.colors.textSecondary
                    }}>
                        No matching entities
                    </div>
                )}
            </div>
        </div>
    );
}

function EntityBreadcrumbs({ data, styles, config, userState, statePath, onEvent, callbacks, utilities }) {
    const [localState, setLocalState] = React.useState({
        focusedCrumbIdx: null,
        ...userState
    });

    // Helper to emit state change events
    const updateState = (updates) => {
        const newState = { ...localState, ...updates };
        setLocalState(newState);
        onEvent && onEvent({
            type: 'stateChanged',
            source: 'EntityBreadcrumbs',
            payload: {
                statePath: statePath,
                newState
            },
            bubbles: true
        });
    };

    // Defensive missing data boundary
    if (!data || !data.path || !Array.isArray(data.path) || data.path.length === 0) {
        return (
            <div style={{
                padding: styles.spacing.md,
                color: styles.colors.textSecondary
            }}>
                No navigation path available
            </div>
        );
    }

    // BreadcrumbTrail is an array of entity IDs
    const breadcrumbIds = data.path;
    // data.entities: [{ID, Name}, ...]
    const entitiesById = (data.entities || []).reduce((acc, entity) => {
        acc[entity.ID] = entity;
        return acc;
    }, {});
    // Map path IDs to display names
    const crumbs = breadcrumbIds.map(id => ({
        id,
        name: entitiesById[id]?.Name || id // fallback to ID
    }));

    // Navigation Event Handler
    const handleCrumbClick = (idx) => {
        // Only allow navigating to ancestors (not current entity)
        if (idx < crumbs.length - 1) {
            onEvent && onEvent({
                type: 'itemSelected',
                source: 'EntityBreadcrumbs',
                payload: {
                    itemId: crumbs[idx].id,
                    item: entitiesById[crumbs[idx].id],
                    selectedCrumbIdx: idx,
                    statePath
                },
                bubbles: true
            });
        }
    };

    // Keyboard navigation: left/right arrows to move, enter/space to activate
    const crumbListRef = React.useRef();
    React.useEffect(() => {
        // Restore focus to current crumb if needed (for keyboard navigation)
        if (
            localState.focusedCrumbIdx != null &&
            crumbListRef.current &&
            crumbListRef.current.children[localState.focusedCrumbIdx]
        ) {
            crumbListRef.current.children[localState.focusedCrumbIdx].focus();
        }
    }, [localState.focusedCrumbIdx]);

    const handleCrumbKeyDown = (e, idx) => {
        // Only allow arrow navigation for actual crumb button (not separator)
        if (e.key === 'ArrowLeft' && idx > 0) {
            e.preventDefault();
            updateState({ focusedCrumbIdx: idx - 1 });
        } else if (e.key === 'ArrowRight' && idx < crumbs.length - 1) {
            e.preventDefault();
            updateState({ focusedCrumbIdx: idx + 1 });
        } else if ((e.key === 'Enter' || e.key === ' ') && idx < crumbs.length - 1) {
            // Only permit navigation for ancestors
            handleCrumbClick(idx);
        }
    };

    // Styles
    const rootStyle = {
        display: 'flex',
        alignItems: 'center',
        gap: styles.spacing.xs,
        padding: styles.spacing.sm,
        background: styles.colors.surface,
        borderRadius: styles.borders.radius,
        fontSize: styles.typography.fontSize.md,
        overflowX: 'auto',
        height: '100%',
        width: '100%'
    };
    const crumbBtnStyle = (isActive, isFocusable) => ({
        cursor: isActive ? 'pointer' : 'default',
        background: isActive
            ? styles.colors.primaryLight || styles.colors.primary
            : 'transparent',
        color: isActive
            ? styles.colors.primary
            : styles.colors.text,
        border: 'none',
        padding: styles.spacing.xs,
        borderRadius: styles.borders.radius,
        outline: isFocusable ? `2px solid ${styles.colors.primary}` : 'none',
        fontWeight: isActive ? 600 : 400,
        transition: styles.transitions?.normal,
        minWidth: 0
    });

    return (
        <nav
            aria-label="Breadcrumb"
            style={rootStyle}
        >
            <ol
                ref={crumbListRef}
                style={{
                    display: 'flex',
                    alignItems: 'center',
                    listStyle: 'none',
                    margin: 0,
                    padding: 0,
                    gap: styles.spacing.xs,
                    width: '100%'
                }}
            >
                {crumbs.map((crumb, idx) => (
                    <React.Fragment key={crumb.id}>
                        <li style={{display: 'flex', alignItems: 'center'}}>
                            <button
                                aria-current={idx === crumbs.length - 1 ? 'page' : undefined}
                                aria-label={
                                    idx === crumbs.length - 1
                                        ? `Current: ${crumb.name}`
                                        : `Go to ${crumb.name}`
                                }
                                tabIndex={
                                    localState.focusedCrumbIdx === idx ? 0 : -1
                                }
                                style={crumbBtnStyle(
                                    idx < crumbs.length - 1, // clickable ancestor
                                    localState.focusedCrumbIdx === idx
                                )}
                                onClick={() => handleCrumbClick(idx)}
                                onFocus={() => updateState({ focusedCrumbIdx: idx })}
                                onKeyDown={(e) => handleCrumbKeyDown(e, idx)}
                                disabled={idx === crumbs.length - 1}
                                type="button"
                            >
                                {crumb.name}
                            </button>
                        </li>
                        {idx < crumbs.length - 1 && (
                            <li aria-hidden="true" style={{ color: styles.colors.textSecondary, userSelect: 'none', margin: `0 ${styles.spacing.xs}` }}>
                                /
                            </li>
                        )}
                    </React.Fragment>
                ))}
            </ol>
        </nav>
    );
}

