function SchemaDiagram({ data, styles, config, userState, statePath, onEvent, callbacks, utilities }) {
  const [localState, setLocalState] = React.useState({
    pan: { x: 0, y: 0 },
    zoom: 1,
    focusedEntityID: (userState && userState.focusedEntityID) || null,
    hoveredNodeID: null,
    ...userState
  });

  // Helpers
  const svgRef = React.useRef();
  const [isPanning, setIsPanning] = React.useState(false);
  const [panStart, setPanStart] = React.useState(null);

  // Get Entities and Relationships from data - trusted directly
  const entityList = data?.Entities || [];
  const relationshipList = data?.['Entity Relationships'] || [];

  // Find focused entity (from userState, or default to first)
  const focusedID = localState.focusedEntityID || (entityList.length ? entityList[0].ID : null);
  const focusedEntity = entityList.find(e => e.ID === focusedID);

  // Compute nodes: include focused, direct parents/children, and referenced entities
  let nodes = [];
  if (focusedEntity) {
    // Add focused for sure
    nodes.push({ ...focusedEntity, isFocused: true });

    // Relationships where focused is parent or child
    relationshipList.forEach(r => {
      if (r.EntityID === focusedEntity.ID) {
        // focused is parent/source, add child if not present
        if (!nodes.find(n => n.ID === r.RelatedEntityID)) {
          const related = entityList.find(e => e.ID === r.RelatedEntityID);
          if (related) nodes.push({ ...related, isDirect: true });
        }
      }
      if (r.RelatedEntityID === focusedEntity.ID) {
        // focused is child/target, add parent if not present
        if (!nodes.find(n => n.ID === r.EntityID)) {
          const related = entityList.find(e => e.ID === r.EntityID);
          if (related) nodes.push({ ...related, isDirect: true });
        }
      }
    });
  }

  // Compute edges: only those between present nodes
  const nodeIDs = nodes.map(n => n.ID);
  const edges = relationshipList.filter(r => nodeIDs.includes(r.EntityID) && nodeIDs.includes(r.RelatedEntityID));

  // Basic force-directed layout
  // Position focused center, rest around circle
  const centerX = 400, centerY = 300;
  const nodeRadius = 38; // px
  let nodePositions = {};
  if (nodes.length) {
    const angleStep = (2 * Math.PI) / Math.max(1, nodes.length - 1);
    nodes.forEach((n, idx) => {
      if (n.isFocused) {
        nodePositions[n.ID] = { x: centerX, y: centerY };
      } else {
        const angle = angleStep * (idx - (n.isFocused ? 0 : (focusedEntity ? 1 : 0)));
        nodePositions[n.ID] = {
          x: centerX + Math.cos(angle) * 170,
          y: centerY + Math.sin(angle) * 140
        };
      }
    });
  }

  // Pan/zoom Events
  const onWheel = e => {
    e.preventDefault();
    let zoom = Math.max(0.5, Math.min(2.5, localState.zoom + (e.deltaY ? -e.deltaY * 0.001 : 0)));
    setLocalState({ ...localState, zoom });
    onEvent && onEvent({
      type: 'stateChanged',
      source: 'SchemaDiagram',
      payload: { statePath, newState: { ...localState, zoom } },
      bubbles: true
    });
  };

  const onMouseDown = e => {
    if (e.button !== 0) return;
    setIsPanning(true);
    setPanStart({ x: e.clientX, y: e.clientY, ...localState.pan });
  };
  const onMouseMove = e => {
    if (!isPanning || !panStart) return;
    const dx = e.clientX - panStart.x;
    const dy = e.clientY - panStart.y;
    const pan = { x: panStart.x + dx - panStart.x + panStart.x, y: panStart.y + dy - panStart.y + panStart.y };
    const newPan = { x: panStart.x + dx - panStart.x, y: panStart.y + dy - panStart.y };
    setLocalState(ls => ({ ...ls, pan: { x: panStart.x + dx - panStart.x, y: panStart.y + dy - panStart.y } }));
    onEvent && onEvent({
      type: 'stateChanged',
      source: 'SchemaDiagram',
      payload: { statePath, newState: { ...localState, pan: { x: panStart.x + dx - panStart.x, y: panStart.y + dy - panStart.y } } },
      bubbles: true
    });
  };
  const onMouseUp = () => {
    setIsPanning(false);
    setPanStart(null);
  };

  React.useEffect(() => {
    if (!isPanning) return;
    const move = e => onMouseMove(e);
    const up = () => onMouseUp();
    window.addEventListener('mousemove', move);
    window.addEventListener('mouseup', up);
    return () => {
      window.removeEventListener('mousemove', move);
      window.removeEventListener('mouseup', up);
    };
  });

  // Show/hide
  const visible = (typeof config?.display !== 'undefined') ? !!config.display : true;
  if (!visible) return null;
  // Accessible colors
  const theme = styles || {};

  // Tooltip
  const [tooltip, setTooltip] = React.useState(null);

  // Tooltip timer
  const tooltipRef = React.useRef();

  // Handlers
  const nodeMouseEnter = (n, evt) => {
    setLocalState(ls => ({ ...ls, hoveredNodeID: n.ID }));
    setTooltip({
      x: evt.clientX,
      y: evt.clientY,
      name: n.Name,
      summary: n.isFocused ? 'Focused entity' : n.isDirect ? 'Directly related' : 'Related',
      entity: n
    });
  };
  const nodeMouseLeave = () => {
    setLocalState(ls => ({ ...ls, hoveredNodeID: null }));
    setTooltip(null);
  };
  const nodeClick = n => {
    setLocalState(ls => ({ ...ls, focusedEntityID: n.ID }));
    onEvent && onEvent({
      type: 'itemSelected',
      source: 'SchemaDiagram',
      payload: { itemId: n.ID, item: n, statePath, newState: { ...localState, focusedEntityID: n.ID } },
      bubbles: true
    });
  };

  // Keyboard pan/zoom
  const onKeyDown = e => {
    let handled = false;
    let { pan, zoom } = localState;
    if (e.key === 'ArrowUp') { pan = { x: pan.x, y: pan.y - 24 }; handled = true; }
    if (e.key === 'ArrowDown') { pan = { x: pan.x, y: pan.y + 24 }; handled = true; }
    if (e.key === 'ArrowLeft') { pan = { x: pan.x - 24, y: pan.y }; handled = true; }
    if (e.key === 'ArrowRight') { pan = { x: pan.x + 24, y: pan.y }; handled = true; }
    if (e.key === '+') { zoom = Math.min(2.5, zoom + 0.1); handled = true; }
    if (e.key === '-') { zoom = Math.max(0.5, zoom - 0.1); handled = true; }
    if (handled) {
      setLocalState(ls => ({ ...ls, pan, zoom }));
      onEvent && onEvent({
        type: 'stateChanged',
        source: 'SchemaDiagram',
        payload: { statePath, newState: { ...localState, pan, zoom } },
        bubbles: true
      });
      e.preventDefault();
    }
  };

  // Layout
  return (
    <div
      tabIndex={0}
      onKeyDown={onKeyDown}
      style={{
        height: '100%',
        width: '100%',
        position: 'relative',
        outline: 'none',
        background: styles.colors.surface,
        border: `${styles.borders.width} solid ${styles.colors.border}`,
        borderRadius: styles.borders.radius,
        userSelect: 'none',
        overflow: 'hidden',
        touchAction: 'none'
      }}
      aria-label="Entity schema diagram"
    >
      {/* SVG Graph */}
      <svg
        ref={svgRef}
        width="100%" height="100%" 
        viewBox={`0 0 800 600`}
        style={{ display: 'block', width: '100%', height: '100%', cursor: isPanning ? 'grabbing' : 'grab'}}
        onPointerDown={onMouseDown}
        onWheel={onWheel}
      >
        <g transform={`translate(${localState.pan.x},${localState.pan.y}) scale(${localState.zoom})`}>
          {/* Edges */}
          {edges.map((e, i) => {
            const src = nodePositions[e.EntityID];
            const tgt = nodePositions[e.RelatedEntityID];
            if (!src || !tgt) return null;
            // Arrow tip
            const dx = tgt.x - src.x, dy = tgt.y - src.y, L = Math.sqrt(dx*dx+dy*dy);
            const tx = tgt.x - nodeRadius * dx / L, ty = tgt.y - nodeRadius * dy / L;
            const sx = src.x + nodeRadius * dx / L, sy = src.y + nodeRadius * dy / L;
            return (
              <g key={i}>
                <line 
                  x1={sx} y1={sy} x2={tx} y2={ty}
                  stroke={styles.colors.primary}
                  strokeWidth={3}
                  markerEnd="url(#arrowhead)"
                  opacity={0.88}
                />
                {/* Relationship label */}
                <text
                  x={(sx+tx)/2 + 10}
                  y={(sy+ty)/2 - 12}
                  style={{
                    fontSize: styles.typography.fontSize.sm,
                    fill: styles.colors.textSecondary,
                    pointerEvents: 'none',
                    textAnchor: 'middle',
                    fontFamily: styles.typography.fontFamily
                  }}
                >
                  {e.DisplayName || e.Type}
                </text>
              </g>
            );
          })}
          {/* Arrow marker */}
          <defs>
            <marker id="arrowhead" markerWidth="9" markerHeight="7" refX="7" refY="3.5" orient="auto" markerUnits="strokeWidth">
              <polygon points="0 0, 9 3.5, 0 7" fill={styles.colors.primary}/>
            </marker>
          </defs>
          {/* Nodes */}
          {nodes.map((n,i) => {
            const pos = nodePositions[n.ID];
            const focused = !!n.isFocused;
            const hovered = localState.hoveredNodeID === n.ID;
            return (
              <g
                key={n.ID}
                tabIndex={0}
                aria-label={n.Name}
                role="button"
                onMouseEnter={e=>nodeMouseEnter(n,e)}
                onMouseLeave={nodeMouseLeave}
                onClick={()=>nodeClick(n)}
                onFocus={e=>nodeMouseEnter(n,{clientX:pos.x,clientY:pos.y})}
                onBlur={nodeMouseLeave}
                style={{ cursor:'pointer' }}
              >
                <circle
                  cx={pos.x}
                  cy={pos.y}
                  r={nodeRadius}
                  fill={focused ? styles.colors.primary : hovered ? styles.colors.primaryLight : styles.colors.surface}
                  stroke={focused ? styles.colors.primary : styles.colors.border}
                  strokeWidth={focused ? 6 : 3}
                  aria-label={n.Name}
                />
                <text
                  x={pos.x}
                  y={pos.y+4}
                  textAnchor="middle"
                  style={{
                    fontSize: focused ? styles.typography.fontSize.lg : styles.typography.fontSize.md,
                    fill: focused ? '#fff' : styles.colors.text,
                    fontWeight: 500,
                    fontFamily: styles.typography.fontFamily,
                    pointerEvents: 'none',
                    userSelect: 'none'
                  }}
                >
                  {n.Name}
                </text>
                {focused && (
                  <text
                    x={pos.x}
                    y={pos.y+nodeRadius+20}
                    textAnchor="middle"
                    style={{ fontSize: styles.typography.fontSize.sm, fill: styles.colors.textSecondary, fontFamily: styles.typography.fontFamily }}
                  >
                    (active)
                  </text>
                )}
              </g>
            );
          })}
        </g>
      </svg>
      {/* Tooltip */}
      {tooltip && (
        <div
          style={{
            position: 'fixed',
            top: tooltip.y+10,
            left: tooltip.x+10,
            background: styles.colors.surface,
            color: styles.colors.text,
            border: `1px solid ${styles.colors.border}`,
            borderRadius: styles.borders.radius,
            padding: styles.spacing.sm,
            pointerEvents: 'none',
            boxShadow: styles.shadows?.md || '0 1px 12px rgba(0,0,0,0.09)',
