function EntitySearchFilterBar({ data, styles, config, userState, statePath, onEvent, callbacks, utilities }) {
    const [localState, setLocalState] = React.useState({
        searchQuery: '',
        selectedTypes: [],
        selectedPermissions: [],
        ...userState
    });

    const debounceMs = 300;
    const debounceTimer = React.useRef();

    // Get dynamic metadata from MemberJunction
    const entityMetadata = utilities?.md?.entities || [];
    // Fallback: if dynamic not available, try data.data_item_0
    // (Parent will provide correctly)
    const entities = entityMetadata.length > 0 ? entityMetadata : (Array.isArray(data) ? data : data?.Entities || []);

    const allEntityTypes = React.useMemo(() => {
        // Use unique SchemaName for filter chips
        const types = entities.map(e => e.SchemaName).filter(Boolean);
        return Array.from(new Set(types));
    }, [entities]);

    const allPermissions = React.useMemo(() => {
        // Look for field-level permission hints (simulate as needed)
        // For demo, show just: ['Read', 'Write', 'Delete']
        return ['Read', 'Write', 'Delete'];
    }, []);

    const updateStateAndNotify = (newPartialState) => {
        const newState = { ...localState, ...newPartialState };
        setLocalState(newState);
        if (onEvent) {
            onEvent({
                type: 'stateChanged',
                source: 'EntitySearchFilterBar',
                payload: { statePath, newState },
                bubbles: true
            });
        }
        // If a parent wants a callback for query/filter change
        if (config?.onSearchChange && typeof config.onSearchChange === 'function') {
            config.onSearchChange(newState.searchQuery);
        }
        if (config?.onFilterChange && typeof config.onFilterChange === 'function') {
            config.onFilterChange({
                types: newState.selectedTypes,
                permissions: newState.selectedPermissions
            });
        }
    };

    const handleQueryChange = (e) => {
        const query = e.target.value;
        setLocalState(prev => ({ ...prev, searchQuery: query }));
        if (debounceTimer.current) clearTimeout(debounceTimer.current);
        debounceTimer.current = setTimeout(() => {
            updateStateAndNotify({ searchQuery: query });
        }, debounceMs);
    };

    // For a11y: input refs
    const searchBoxRef = React.useRef(null);

    // Filter handers (no debounce, instant update)
    const handleTypeToggle = (type) => {
        let newSelected;
        if (localState.selectedTypes.includes(type)) {
            newSelected = localState.selectedTypes.filter(t => t !== type);
        } else {
            newSelected = [...localState.selectedTypes, type];
        }
        updateStateAndNotify({ selectedTypes: newSelected });
    };

    const handlePermissionToggle = (permission) => {
        let newSelected;
        if (localState.selectedPermissions.includes(permission)) {
            newSelected = localState.selectedPermissions.filter(p => p !== permission);
        } else {
            newSelected = [...localState.selectedPermissions, permission];
        }
        updateStateAndNotify({ selectedPermissions: newSelected });
    };

    // ARIA labels for accessibility
    const typeAriaLabel = 'Filter by entity type';
    const permAriaLabel = 'Filter by permission';

    return (
        <div style={{
            background: styles.colors.surface,
            padding: styles.spacing.sm,
            borderRadius: styles.borders.radius,
            boxShadow: styles.shadows?.sm,
            display: 'flex',
            flexDirection: 'column',
            gap: styles.spacing.sm
        }}>
            <div style={{ display: 'flex', alignItems: 'center', gap: styles.spacing.sm }}>
                <input
                    ref={searchBoxRef}
                    type="search"
                    aria-label="Global entity search"
                    placeholder="Search entities..."
                    value={localState.searchQuery}
                    onChange={handleQueryChange}
                    style={{
                        flex: 1,
                        padding: styles.spacing.sm,
                        fontSize: styles.typography.fontSize.md,
                        borderRadius: styles.borders.radius,
                        border: `1px solid ${styles.colors.border}`,
                        outline: 'none',
                        background: styles.colors.background
                    }}
                />
            </div>
            <div style={{ display: 'flex', gap: styles.spacing.md, alignItems: 'center' }}>
                <div style={{ display: 'flex', alignItems: 'center', gap: styles.spacing.xs }} aria-label={typeAriaLabel}>
                    {allEntityTypes.map(type => (
                        <button
                            key={type}
                            type="button"
                            aria-pressed={localState.selectedTypes.includes(type)}
                            tabIndex={0}
                            onClick={() => handleTypeToggle(type)}
                            style={{
                                padding: `${styles.spacing.xs} ${styles.spacing.sm}`,
                                background: localState.selectedTypes.includes(type) ? styles.colors.primary : styles.colors.surface,
                                color: localState.selectedTypes.includes(type) ? '#fff' : styles.colors.text,
                                border: `1px solid ${styles.colors.border}`,
                                borderRadius: styles.borders.radius,
                                fontSize: styles.typography.fontSize.sm,
                                cursor: 'pointer',
                                outline: 'none',
                                transition: 'background 0.15s',
                                boxShadow: styles.shadows?.xs
                            }}
                            aria-label={`Toggle filter for ${type}`}
                        >
                            {type}
                        </button>
                    ))}
                </div>
                <div style={{ display: 'flex', alignItems: 'center', gap: styles.spacing.xs }} aria-label={permAriaLabel}>
                    {allPermissions.map(permission => (
                        <button
                            key={permission}
                            type="button"
                            aria-pressed={localState.selectedPermissions.includes(permission)}
                            tabIndex={0}
                            onClick={() => handlePermissionToggle(permission)}
                            style={{
                                padding: `${styles.spacing.xs} ${styles.spacing.sm}`,
                                background: localState.selectedPermissions.includes(permission) ? styles.colors.secondary : styles.colors.surface,
                                color: localState.selectedPermissions.includes(permission) ? '#fff' : styles.colors.text,
                                border: `1px solid ${styles.colors.border}`,
                                borderRadius: styles.borders.radius,
                                fontSize: styles.typography.fontSize.sm,
                                cursor: 'pointer',
                                outline: 'none',
                                transition: 'background 0.15s',
                                boxShadow: styles.shadows?.xs
                            }}
                            aria-label={`Toggle filter for ${permission} permission`}
                        >
                            {permission}
                        </button>
                    ))}
                </div>
            </div>
        </div>
    );
}

function FieldDetailDrawer({ data, styles, config, userState, statePath, onEvent, callbacks, utilities }) {
    const [localState, setLocalState] = React.useState({ ...userState });
    const [loading, setLoading] = React.useState(false);
    const [fieldDetail, setFieldDetail] = React.useState(null);

    // Expect parent to pass focusedField; if not, try to fetch via dynamic data
    const field = data?.field || null;
    const visible = Boolean(data?.visible);
    const AntdDrawer = window.antd ? window.antd.Drawer : null;
    const AntdTabs = window.antd ? window.antd.Tabs : null;
    const AntdTable = window.antd ? window.antd.Table : null;
    const AntdSpin = window.antd ? window.antd.Spin : null;
    const AntdTag = window.antd ? window.antd.Tag : null;
    const AntdButton = window.antd ? window.antd.Button : null;
    const TabPane = AntdTabs ? AntdTabs.TabPane : null;

    React.useEffect(() => {
        if (!utilities || !utilities.rv || !field || fieldDetail?.ID === field.ID) return;
        setLoading(true);
        utilities.rv
            .runView({
                EntityName: 'Entity Fields',
                ExtraFilter: `ID = '${field.ID}'`,
                ResultType: 'simple',
                MaxRows: 1
            })
            .then(result => {
                if (result && result.Success && result.Results && result.Results.length > 0) {
                    setFieldDetail(result.Results[0]);
                } else {
                    setFieldDetail(null);
                }
            })
            .finally(() => setLoading(false));
    }, [field && field.ID]);

    const mergedField = fieldDetail || field;
    const handleClose = () => {
        if (onEvent) onEvent({
            type: 'actionTriggered',
            source: 'FieldDetailDrawer',
            payload: { action: 'close' },
            bubbles: true
        });
    };

    // Accessibility - keyboard trap & Escape
    React.useEffect(() => {
        if (!visible) return;
        function onKeyDown(e) {
            if (e.key === 'Escape') handleClose();
        }
        window.addEventListener('keydown', onKeyDown);
        return () => window.removeEventListener('keydown', onKeyDown);
    }, [visible]);

    if (!AntdDrawer || !AntdTabs || !AntdTable) {
        return <div style={{ padding: styles?.spacing?.xl, textAlign: 'center', color: styles?.colors?.textSecondary }}>Ant Design library is required for this component</div>;
    }
    if (!visible || !mergedField) return null;

    // Simulate usage stats and version history (as fetching is not detailed in dataRequirements)
    const dummyUsage = [
