                {dummyUsage.map((row, i) => (
                    <tr key={i}>
                        <td style={{ fontWeight: 500, padding: styles.spacing.xs }}>{row.label}</td>
                        <td style={{ padding: styles.spacing.xs }}>{row.value}</td>
                    </tr>
                ))}
            </tbody>
