    const VersionTable = () => (
        <table style={{ width: '100%', borderCollapse: 'collapse', fontSize: styles.typography.fontSize.md }}>
            <thead>
                <tr>
                    <th style={{ padding: styles.spacing.xs, fontWeight: 500 }}>Version</th>
