function SalesReportByDate({ utilities }) {
  const [data, setData] = React.useState([]);

  React.useEffect(() => {
    async function loadData() {
      const result = await utilities.rq.RunQuery({
        QueryName: 'Sales By Date Range',
        CategoryPath: 'Reports/Sales',
        Parameters: {
          StartDate: '',
          EndDate: ''
        }
      });
      if (result?.Success) {
        setData(result.Results || []);
      }
    }
    loadData();
  }, []);

  return (
    <div>
      <h1>Sales Report</h1>
      <div>Total Records: {data.length}</div>
    </div>
  );
}
