function TestComponent({ utilities, savedUserSettings, onSavedUserSettingsChange }) {
  const [data, setData] = useState([]);

  useEffect(() => {
    async function loadData() {
      // Calling 'SalesData' but spec defines 'SalesReport'
      const result = await utilities.rq.RunQuery({
        QueryName: 'SalesData',
        CategoryPath: 'Reports/Sales'
      });
      if (result.Success) {
        setData(result.Results || []);
      }
    }
    loadData();
  }, []);

  return (
    <div>
      <h1>Sales Report</h1>
      <p>Records: {data.length}</p>
    </div>
  );
}
