function SimpleChartAverageMissingValuefield({ components, utilities }) {
  const { SimpleChart } = components;
  const [orders, setOrders] = React.useState(null);

  React.useEffect(() => {
    async function loadData() {
      const results = await utilities.rv.RunView({
        EntityName: 'Orders',
        ExtraFilter: '',
        MaxRows: 100
      });
      setOrders(results.Results);
    }
    loadData();
  }, []);

  if (!orders) return <div>Loading...</div>;

  return (
    <SimpleChart
      entityName="Orders"
      data={orders}
      categoryField="Status"
      aggregateMethod="average"
      chartType="bar"
      title="Average Orders by Status"
      utilities={utilities}
    />
  );
}
