function TestComponent({ utilities, savedUserSettings, onSavedUserSettingsChange }) {
  const [data, setData] = useState([]);

  useEffect(() => {
    async function loadData() {
      const result = await utilities.rv.RunView({
        EntityName: 'Documents',
        MaxRows: 100
      });
      if (result.Success) {
        // Wrong: using replace() instead of replaceAll() for multiple occurrences
        const processed = result.Results.map(doc => ({
          ...doc,
          // This only replaces first occurrence
          Content: doc.Content.replace('old', 'new'),
          // This only replaces first space
          Title: doc.Title.replace(' ', '_'),
          // Should use replaceAll or regex with global flag
          Description: doc.Description.replace('\n', '<br>')
        }));
        setData(processed);
      }
    }
    loadData();
  }, []);

  const formatText = (text) => {
    // Wrong: multiple replace calls that only replace first occurrence
    return text
      .replace('<', '&lt;')
      .replace('>', '&gt;')
      .replace('"', '&quot;');
  };

  return (
    <div>
      <h1>Documents</h1>
      {data.map(doc => (
        <div key={doc.ID}>
          <h2>{doc.Title}</h2>
          <p>{formatText(doc.Content)}</p>
        </div>
      ))}
    </div>
  );
}
