function TestComponent({ utilities, savedUserSettings, onSavedUserSettingsChange }) {
  const [data, setData] = useState([]);

  const userInput = savedUserSettings.searchTerm || '';
  const filter = `Name LIKE '%${userInput}%'`;

  useEffect(() => {
    async function loadData() {
      const result = await utilities.rv.RunView({
        EntityName: 'Users',
        ExtraFilter: filter
      });
      setData(result.Results || []);
    }
    loadData();
  }, [filter]);

  return (
    <div>
      <h1>Test Component</h1>
      <p>Data count: {data.length}</p>
    </div>
  );
}
