function DataGridColumndefMixed({ components, utilities }) {
  const { DataGrid } = components;
  const [products, setProducts] = React.useState(null);

  React.useEffect(() => {
    async function loadData() {
      const results = await utilities.rv.RunView({
        EntityName: 'Products',
        ExtraFilter: '',
        MaxRows: 50
      });
      setProducts(results.Results);
    }
    loadData();
  }, []);

  if (!products) return <div>Loading...</div>;

  return (
    <DataGrid
      entityName="Products"
      data={products}
      columns={[
        'SKU',
        { field: 'Name', header: 'Product Name' },
        { field: 'Price', header: 'Price', width: 100 },
        'Category'
      ]}
      utilities={utilities}
    />
  );
}
