function QueryMixedParamsOnlyRequiredComponent({ utilities, year }) {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function loadData() {
      try {
        // Query has 1 required param (Year) and 2 optional params (Quarter, Region)
        // Providing only required is valid
        const result = await utilities.rq.RunQuery({
          QueryName: 'SalesReport',
          CategoryPath: 'Reports/Sales',
          Parameters: {
            Year: year
            // Quarter and Region omitted - valid because optional
          }
        });

        if (result.Success) {
          setData(result.Results);
        }
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    }
    loadData();
  }, [year]);

  if (loading) return <div>Loading...</div>;

  return (
    <div>
      <h1>Sales Report {year}</h1>
      <p>Count: {data.length}</p>
    </div>
  );
}
