function SalesReportComponent({ utilities, savedUserSettings, onSavedUserSettingsChange }) {
  const { useState, useEffect } = React;
  const [reportData, setReportData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    async function loadReport() {
      try {
        const result = await utilities.rq.RunQuery({
          QueryName: 'MonthlySalesReport',
          CategoryPath: 'Reports/Sales',
          Parameters: {
            // CORRECT: Number parameters get number values
            year: 2024,
            month: 11,
            // CORRECT: String parameters get string values
            region: 'North America',
            // CORRECT: Boolean parameters get boolean values
            includeReturns: true
          }
        });

        // CORRECT: Check result.Success before accessing Results
        if (result.Success) {
          setReportData(result.Results || []);
        } else {
          setError(result.ErrorMessage || 'Failed to load report');
        }
      } catch (err) {
        setError('An unexpected error occurred');
      } finally {
        setLoading(false);
      }
    }
    loadReport();
  }, []);

  if (loading) return <div>Loading report...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="sales-report">
      <h1>Monthly Sales Report</h1>
      <p>Total records: {reportData.length}</p>
      <table>
        <thead>
          <tr>
            <th>Product</th>
            <th>Amount</th>
            <th>Date</th>
          </tr>
        </thead>
        <tbody>
          {reportData.map((row, index) => (
            <tr key={index}>
              {/* CORRECT: Only accessing fields declared in query definition */}
              <td>{row.ProductName}</td>
              <td>${row.Amount}</td>
              <td>{row.SaleDate}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
