function SimpleDrilldownValidGridFields({ components, utilities }) {
  const { SimpleDrilldownChart } = components;
  const [deals, setDeals] = React.useState(null);

  React.useEffect(() => {
    async function loadData() {
      const results = await utilities.rv.RunView({
        EntityName: 'Deals',
        ExtraFilter: '',
        MaxRows: 1000
      });
      setDeals(results.Results);
    }
    loadData();
  }, []);

  if (!deals) return <div>Loading...</div>;

  return (
    <div>
      <h1>Deals by Stage</h1>
      <SimpleDrilldownChart
        entityName="Deals"
        data={deals}
        groupBy="Stage"
        valueField="Amount"
        aggregateMethod="sum"
        chartType="bar"
        gridFields={['Name', 'Account', 'Amount', 'Stage']}
        entityPrimaryKeys={['ID']}
        utilities={utilities}
      />
    </div>
  );
}
