function TestComponent({ utilities, savedUserSettings, onSavedUserSettingsChange }) {
  const [data, setData] = useState([]);

  useEffect(() => {
    async function loadData() {
      const result = await utilities.rv.RunView({
        EntityName: 'Invoices',
        OrderBy: 'CreatedDate DESC',  // CreatedDate not in sortFields
        MaxRows: 100
      });
      if (result.Success) {
        setData(result.Results || []);
      }
    }
    loadData();
  }, []);

  return (
    <div>
      <h1>Invoices</h1>
      <p>Count: {data.length}</p>
    </div>
  );
}
