function MembershipTypeChart({ utilities, styles, components, callbacks, savedUserSettings, onSaveUserSettings, queryName = "Active Members By Membership Type", showActiveOnly = true }) {
  const [chartData, setChartData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // âŒ BAD: utilities.rq.RunQuery in dependency array causes infinite loop
  useEffect(() => {
    const loadChart = async () => {
      setLoading(true);
      setError(null);
      try {
        const result = await utilities.rq.RunQuery({
          QueryName: queryName,
          CategoryPath: 'Skip/Memberships/Analytics',
          Parameters: showActiveOnly ? { Status: 'Active' } : {}
        });
        if (result.Success) {
          setChartData(result.Results || []);
        } else {
          setError(result.ErrorMessage || 'Failed to load chart data');
        }
      } catch (err) {
        setError(err.message || 'Error loading chart data');
      } finally {
        setLoading(false);
      }
    };
    loadChart();
  }, [queryName, showActiveOnly, utilities.rq.RunQuery]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div>
      <h2>Membership Type Chart</h2>
      <pre>{JSON.stringify(chartData, null, 2)}</pre>
    </div>
  );
}
