function ChildComponent({ utilities, savedUserSettings, onSavedUserSettingsChange, callbacks }) {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    async function loadUsers() {
      const result = await utilities.rv.RunView({ EntityName: 'Users' });
      if (result.Success) {
        setUsers(result.Results || []);
      }
    }
    loadUsers();
  }, []);

  return (
    <div>
      <h1>Child Component</h1>
      <ul>
        {users.map(user => <li key={user.ID}>{user.Name}</li>)}
      </ul>
    </div>
  );
}
