function QueryFieldInvalidComponent({ utilities, savedUserSettings, onSavedUserSettingsChange }) {
  const { useState, useEffect } = React;
  const [salesData, setSalesData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    async function loadSalesData() {
      try {
        const result = await utilities.rq.RunQuery({
          QueryName: 'SalesReport',
          CategoryPath: 'Reports/Sales',
          Parameters: {
            year: 2024
          }
        });

        if (result.Success) {
          // Mix of valid and invalid field accesses
          const data = result.Results.map(row => ({
            // ✅ Valid fields - these exist in the query definition
            account: row.AccountName,
            revenue: row.Revenue,
            quarter: row.Quarter,
            year: row.Year,

            // ❌ VIOLATION: InvalidField doesn't exist in query definition
            invalid: row.InvalidField,

            // ❌ VIOLATION: NonExistentProperty doesn't exist in query definition
            fake: row.NonExistentProperty,

            // ❌ VIOLATION: Profit field not included in query definition
            profit: row.Profit,

            // ❌ VIOLATION: CustomerName field not included in query definition
            customer: row.CustomerName
          }));
          setSalesData(data);
        } else {
          setError(result.ErrorMessage);
        }
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    }
    loadSalesData();
  }, []);

  if (loading) return <div>Loading sales data...</div>;
  if (error) return <div className="error">Error: {error}</div>;

  return (
    <div className="sales-report">
      <h1>Sales Report 2024</h1>
      <table>
        <thead>
          <tr>
            <th>Account</th>
            <th>Revenue</th>
            <th>Quarter</th>
            <th>Invalid Field</th>
          </tr>
        </thead>
        <tbody>
          {salesData.map((row, index) => (
            <tr key={index}>
              <td>{row.account}</td>
              <td>${row.revenue.toFixed(2)}</td>
              <td>Q{row.quarter}</td>
              {/* Using invalid field in display */}
              <td>{row.invalid}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <div className="summary">
        <p>Total Records: {salesData.length}</p>
        {/* Another invalid field access */}
        <p>Profit: {salesData.reduce((sum, row) => sum + row.profit, 0)}</p>
      </div>
    </div>
  );
}
