function SpreadLosesRequiredFieldFixed({ utilities, components }) {
  const [members, setMembers] = useState([]);
  const { DataGrid } = components;

  useEffect(() => {
    async function loadData() {
      const result = await utilities.rv.RunView({ EntityName: 'Members' });
      if (result?.Success) {
        // âœ… Spread keeps all fields including Email
        const processed = result.Results.map(m => ({
          ...m,
          FullName: `${m.FirstName} ${m.LastName}`
        }));
        setMembers(processed);
      }
    }
    loadData();
  }, []);

  return (
    <div>
      <h3>Members</h3>
      {/* Grid has access to Email field */}
      <DataGrid 
        data={members} 
        columns={[
          {field: 'FullName', title: 'Name'},
          {field: 'Email', title: 'Email'}
        ]} 
      />
    </div>
  );
}
