function ChartDataEmptyFieldsFixed({ utilities, components }) {
  const [chartData, setChartData] = useState([]);
  const { SimpleChart } = components;

  useEffect(() => {
    async function loadData() {
      const result = await utilities.rv.RunView({ EntityName: 'Sales' });
      if (result?.Success) {
        // âœ… Filter keeps valid data with all fields
        const filtered = result.Results.filter(s => s.Amount > 0);
        setChartData(filtered);
      }
    }
    loadData();
  }, []);

  return (
    <div>
      <h3>Regional Sales</h3>
      {/* Chart has properly structured data */}
      <SimpleChart data={chartData} groupBy="Month" valueField="Amount" />
    </div>
  );
}
