function DataGridMixedSourceInvalidFixed({ utilities, components }) {
  const [members, setMembers] = useState([]);
  const { DataGrid } = components;

  useEffect(() => {
    async function loadData() {
      const result = await utilities.rv.RunView({ EntityName: 'Members' });
      if (result?.Success) {
        // Adding computed field
        const enhanced = result.Results.map(m => ({
          ...m,
          FullName: `${m.FirstName} ${m.LastName}`
        }));
        setMembers(enhanced);
      }
    }
    loadData();
  }, []);

  return (
    <div>
      <h3>Members</h3>
      {/* âœ… Using entity field (Email) and computed field (FullName) */}
      <DataGrid 
        data={members} 
        columns={[
          {field: 'FullName', title: 'Name'},
          {field: 'Email', title: 'Email'},
          {field: 'MembershipType', title: 'Type'}
        ]} 
      />
    </div>
  );
}
