function UserListComponent({ utilities, savedUserSettings, onSavedUserSettingsChange }) {
  const { useState, useEffect } = React;
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    async function loadUsers() {
      try {
        const result = await utilities.rv.RunView({
          EntityName: 'Users',
          MaxRows: 100,
          OrderBy: 'Name ASC'
        });

        // CORRECT: Always check result.Success before accessing Results
        if (result.Success) {
          setUsers(result.Results || []);
        } else {
          // CORRECT: Use ErrorMessage property for error handling
          setError(result.ErrorMessage || 'Failed to load users');
        }
      } catch (err) {
        setError('An unexpected error occurred');
      } finally {
        setLoading(false);
      }
    }
    loadUsers();
  }, []);

  if (loading) return <div>Loading users...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="user-list">
      <h1>Users ({users.length})</h1>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Email</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {users.map(user => (
            <tr key={user.ID}>
              {/* CORRECT: Only accessing fields declared in dataRequirements */}
              <td>{user.Name}</td>
              <td>{user.Email}</td>
              <td>{user.Status}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
