function QueryOptionalParamsSomeProvidedComponent({ utilities, category }) {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function loadData() {
      try {
        // Query has 4 optional params
        // Providing only 2 is valid - others use defaults
        const result = await utilities.rq.RunQuery({
          QueryName: 'Invoice Line Items by Product',
          CategoryPath: 'Demo',
          Parameters: {
            Category: category,
            StartDate: '2024-01-01'
            // ProductID and EndDate omitted - valid because optional
          }
        });

        if (result.Success) {
          setData(result.Results);
        }
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    }
    loadData();
  }, [category]);

  if (loading) return <div>Loading...</div>;

  return (
    <div>
      <h1>Line Items for {category}</h1>
      <p>Count: {data.length}</p>
    </div>
  );
}
