function QueryResultFilterInvalidFieldFixed({ utilities }) {
  const [inStockProducts, setInStockProducts] = useState([]);

  useEffect(() => {
    async function loadData() {
      const result = await utilities.rq.RunQuery({
        QueryName: 'Product Catalog',
        CategoryPath: 'Products/Reports'
      });
      if (result?.Success) {
        // âœ… Filtering by StockLevel field that exists in query
        const inStock = result.Results.filter(p => p.StockLevel > 0);
        setInStockProducts(inStock);
      }
    }
    loadData();
  }, []);

  return (
    <div>
      <h3>In Stock Products</h3>
      {inStockProducts.map((p, i) => (
        <div key={i}>{p.ProductName} - ${p.Price}</div>
      ))}
    </div>
  );
}
