function CrossComponentAggregateFieldTypeFixed({ utilities, components }) {
  const [summary, setSummary] = useState([]);
  const { SimpleChart } = components;

  useEffect(() => {
    async function loadData() {
      const result = await utilities.rv.RunView({ EntityName: 'Sales' });
      if (result?.Success) {
        // âœ… Aggregating to array with proper structure for chart
        const byMonth = result.Results.reduce((acc, sale) => {
          const month = sale.SaleDate.substring(0, 7);
          const existing = acc.find(item => item.Month === month);
          if (existing) {
            existing.Total += sale.Amount;
          } else {
            acc.push({ Month: month, Total: sale.Amount });
          }
          return acc;
        }, []);
        setSummary(byMonth);
      }
    }
    loadData();
  }, []);

  return (
    <div>
      <h3>Sales by Month</h3>
      {/* Chart receives array with Month and Total fields */}
      <SimpleChart data={summary} groupBy="Month" valueField="Total" />
    </div>
  );
}
