function CrossComponentEntityToGridFixed({ utilities, components }) {
  const [members, setMembers] = useState([]);
  const { DataGrid } = components;

  useEffect(() => {
    async function loadData() {
      const result = await utilities.rv.RunView({ EntityName: 'Members' });
      if (result?.Success) {
        setMembers(result.Results);
      }
    }
    loadData();
  }, []);

  return (
    <div>
      <h3>Members</h3>
      {/* âœ… Grid references Status field that exists on entity */}
      <DataGrid 
        data={members} 
        columns={[
          {field: 'FirstName', title: 'Name'},
          {field: 'Status', title: 'Status'}
        ]} 
      />
    </div>
  );
}
