function CrossComponentMultipleTransformsFixed({ utilities, components }) {
  const [chartData, setChartData] = useState([]);
  const { SimpleChart } = components;

  useEffect(() => {
    async function loadData() {
      const result = await utilities.rv.RunView({ EntityName: 'Orders' });
      if (result?.Success) {
        // First transformation: add computed field
        const withMonth = result.Results.map(o => ({
          ...o,
          Month: o.OrderDate.substring(0, 7)
        }));

        // âœ… Second transformation: keeps TotalAmount
        const grouped = withMonth.map(o => ({
          Month: o.Month,
          CustomerName: o.CustomerName,
          TotalAmount: o.TotalAmount
        }));

        setChartData(grouped);
      }
    }
    loadData();
  }, []);

  return (
    <div>
      <h3>Orders Chart</h3>
      {/* Chart has access to TotalAmount */}
      <SimpleChart data={chartData} groupBy="Month" valueField="TotalAmount" />
    </div>
  );
}
