function CrossComponentTransformFieldLostFixed({ utilities, components }) {
  const [chartData, setChartData] = useState([]);
  const { SimpleChart } = components;

  useEffect(() => {
    async function loadData() {
      const result = await utilities.rv.RunView({ EntityName: 'Orders' });
      if (result?.Success) {
        // âœ… Transformation includes TotalAmount field that chart needs
        const transformed = result.Results.map(o => ({
          OrderDate: o.OrderDate,
          CustomerName: o.CustomerName,
          TotalAmount: o.TotalAmount
        }));
        setChartData(transformed);
      }
    }
    loadData();
  }, []);

  return (
    <div>
      <h3>Orders by Date</h3>
      {/* Chart has access to TotalAmount field */}
      <SimpleChart data={chartData} groupBy="OrderDate" valueField="TotalAmount" />
    </div>
  );
}
