function PipelineEntityToFilterToGridFixed({ utilities, components }) {
  const [gridData, setGridData] = useState([]);
  const { DataGrid } = components;

  useEffect(() => {
    async function loadData() {
      // Stage 1: Load entity
      const result = await utilities.rv.RunView({ EntityName: 'Members' });

      if (result?.Success) {
        // Stage 2: Filter and transform
        const active = result.Results
          .filter(m => m.Status === 'Active')
          .map(m => ({
            ID: m.ID,
            FullName: `${m.FirstName} ${m.LastName}`, // ✅ Creates FullName field
            Email: m.Email
          }));

        setGridData(active);
      }
    }
    loadData();
  }, []);

  return (
    <div>
      <h3>Active Members</h3>
      {/* ✅ Grid matches FullName field from data */}
      <DataGrid 
        data={gridData} 
        columns={[
          {field: 'FullName', title: 'Member'},
          {field: 'Email', title: 'Email'}
        ]} 
      />
    </div>
  );
}
