function TestComponent({ utilities, savedUserSettings, onSavedUserSettingsChange, parentData, setParentData }) {
  const [localData, setLocalData] = useState([]);

  useEffect(() => {
    async function loadData() {
      const result = await utilities.rv.RunView({
        EntityName: 'Items',
        MaxRows: 100
      });
      if (result.Success) {
        setLocalData(result.Results || []);
        // Child trying to set parent state directly
        setParentData(result.Results);
      }
    }
    loadData();
  }, []);

  const handleClick = (item) => {
    // Child modifying parent data directly
    parentData.push(item);
    setParentData([...parentData]);
  };

  return (
    <div>
      <h1>Items</h1>
      {localData.map(item => (
        <div key={item.ID} onClick={() => handleClick(item)}>
          {item.Name}
        </div>
      ))}
    </div>
  );
}
