function ReportWithLogicalOr({ utilities }) {
  const [data, setData] = React.useState([]);
  const [userDate, setUserDate] = React.useState(null);
  const fallbackDate = '';

  React.useEffect(() => {
    async function loadData() {
      const result = await utilities.rq.RunQuery({
        QueryName: 'Sales Report',
        CategoryPath: 'Reports/Sales',
        Parameters: {
          ReportDate: userDate || fallbackDate
        }
      });
      if (result?.Success) {
        setData(result.Results || []);
      }
    }
    loadData();
  }, [userDate]);

  return (
    <div>
      <h1>Sales Report</h1>
      <input type="date" onChange={(e) => setUserDate(e.target.value)} />
      <div>Records: {data.length}</div>
    </div>
  );
}
