function QueryBitParamInvalidComponent({ utilities, savedUserSettings, onSaveUserSettings }) {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function loadData() {
      try {
        const result = await utilities.rq.RunQuery({
          QueryName: 'ActiveUsers',
          CategoryPath: 'Admin/Users',
          Parameters: {
            // VIOLATION: IsActive should be a boolean, not a string
            IsActive: 'true',
            // VIOLATION: IncludeDeleted should be a boolean, not a string
            IncludeDeleted: 'false',
            // This one is correct - Department is a string
            Department: 'Engineering'
          }
        });

        if (result.Success) {
          setData(result.Results);
        }
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    }
    loadData();
  }, []);

  if (loading) return <div>Loading...</div>;

  return (
    <div>
      <h1>Active Users</h1>
      <ul>
        {data.map(user => (
          <li key={user.ID}>{user.Name} - {user.Email}</li>
        ))}
      </ul>
    </div>
  );
}
