function QueryExtraUnknownParamsComponent({ utilities }) {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function loadData() {
      try {
        const result = await utilities.rq.RunQuery({
          QueryName: 'ActiveUsers',
          CategoryPath: 'Admin/Users',
          Parameters: {
            IsActive: true,
            // WRONG: These params don't exist in query spec
            Department: 'Engineering',
            IncludeDeleted: false,
            Role: 'Admin'
          }
        });

        if (result.Success) {
          setData(result.Results);
        }
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    }
    loadData();
  }, []);

  if (loading) return <div>Loading...</div>;

  return (
    <div>
      <h1>Active Users</h1>
      <p>Count: {data.length}</p>
    </div>
  );
}
